import React from 'react';
import { Checkbox, Table } from 'antd';
import Link from 'next/link';

const TableHomeCategory = ({
  category, editModalOnClick, onSelectAll, onSelectOne,
  selectAll, selectedCatIds, mainPositionOnChange, posotionChangeCategorys, mainCategory, activeQuestionCount
}) => {
  const columns = [
  
    {
      title: 'Name',
      dataIndex: 'category',
      width: '20%',
    },
    {
      title: 'Position',
      dataIndex: 'position',
      width: '5%',
    },
    {
      title: 'Questions',
      dataIndex: 'questions',
      align: 'center',
      width: '12%',
    },
    {
      title: 'Edit',
      dataIndex: 'edit',
      align: 'center',
      width: '5%',
    },
   
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check',
      width: '5%',
    }
  ];

  let data = category.map(a => {
    let position = posotionChangeCategorys.filter(p => p.catId === a.cat_id);
   
    let activeCountFinded = activeQuestionCount.find(aq => aq.sub_id === a.cat_id);

    let obj = {
      key: a.exam_id,
      category: a.exam_name,
      uniqueCode: a.cat_code,
      cat_desc: a.cat_desc,
      position: (
        <input
          type="text"
          onChange={event => mainPositionOnChange(a.cat_id, event.target.value)}
          style={{ width: '50px', textAlign: 'center', padding: '0px' }}
          value={position && position.length > 0 ? position[0].position : a.cat_pos}
          className="form-control form-control-lg"
        />
      ),
      edit: (
        <i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>
      ),
      check: (
        <Checkbox
          onClick={() => onSelectOne(a.cat_id)}
          checked={selectedCatIds.indexOf(a.cat_id) >= 0}
        />
      ),
      pdf: (
        <i className="fas fa-file-pdf" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>
      ),
      questions: (
        <div className='row text-center'>
          <div style={{ backgroundColor: '#4caf50', width: 50, height: 30, border: 'none', borderRadius: 10, padding: 5, marginLeft: 10 }}>
            <Link href={`/examinationCalendar/exams?exam_id=${a.cat_id}&exam_name=${a.exam_name}`}>
            <a style={{ color: '#fff' }}>{activeCountFinded && activeCountFinded.activecount ? activeCountFinded.activecount : 0}</a>
            </Link>
          </div>
        </div>
      ),
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableHomeCategory;
