import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableCoupon from '../../../components/tables/Coupon';

import { getAllCoupon, getInactiveCoupon } from '../../../store/Coupon/action';
import CouponRepository from '../../../repositories/CouponRepository';
import Moment from "moment";

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const valueRef = React.createRef()
    const { auth } = useSelector(({ auth }) => auth);
    const { allCoupon, totalActiveCount,inactiveCoupon, totalInactiveCount } = useSelector(({ Coupon }) => Coupon);

    const [showModal, setShowModal] = useState(false);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCouponId, setSelectedHomeCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [fromDate, setFromDate] = useState(Moment(new Date()).format('MM-DD-YYYY'));
    const [toDate, setToDate] = useState(Moment(new Date()).format('MM-DD-YYYY'));
    const [data, c] = useState([])
    const [couponId, setCouponId] = useState('');
    const [couponName, setCouponName] = useState('');
    const [couponCode, setCouponCode] = useState('');
    const [couponValue, setCouponValue] = useState(0);
    const [couponValueType, setCouponValueType] = useState('');
    const [cartValueRange, setCartValueRange] = useState(0);
    const [noOfUsage, setNoOfUsage] = useState('');
    const [noOfUsageUser, setNoOfUsageUser] = useState('');
    const [cartValueRangeType, setCartValueRangeType] = useState('Less Than');
    const [cartValue, setCartValue] = useState(0);
    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllCoupon(ctr));
        dispatch(getInactiveCoupon(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allCoupon, inactiveCoupon]);

    const addModalOnClick = async () => {
        setLoader(true);
        setCouponName('');
        setCouponCode('');
        setCouponValue('');
        setCouponValueType('');
        setCartValueRange('');
        setNoOfUsage('');
        setNoOfUsageUser('');
        setCartValueRangeType('');
        setCartValue('')
        setLoader(false);
        setShowModal(true);
    }

    const deleteOneDashboardCategory = (data) => {
        Modal.confirm({
            title: 'Confirm',
            content: 'Do you want to delete this Coupon.',
            okText: 'Yes',
            onOk: () => deleteOneCategory(data),
            cancelText: 'No',
        });
    }

    const deleteOneCategory = async (data) => {
        let obj = { "couponId": [data.coupon_id] };
        await CouponRepository.deleteCoupon(obj);
        Modal.success({
            title: 'Coupon Deleted Successfully.'
        });
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllCoupon(ctr));
        dispatch(getInactiveCoupon(ctr));
    }

    const editModalOnClick = async (data) => {
        console.log(data,'12345')
        setLoader(true);
        setCouponName(data.coupon_name);
        setCouponId(data.coupon_id);
        setCouponCode(data.coupon_code);
        setCouponValue(data.coupon_value);
        setCouponValueType(data.coupon_value_type);
        setCartValueRange(data.cart_value_range);
        setNoOfUsage(data.no_of_usage);
        setNoOfUsageUser(data.no_of_usage_user);
        setCartValueRangeType(data.cart_value_range_type);
        setCartValue(data.cart_value)
        setFromDate(Moment(data.from_date).format('MM-DD-YYYY'));
        setToDate(Moment(data.to_date).format('MM-DD-YYYY'));
        setLoader(false);
        setShowModal(true);
    } 
    

    const closeModalOnClick = () => {
        setCouponName('');
        setCouponId('');
        setErrors({});
        setShowModal(false);
    }
    const handleTypeChange = async (event, value) => {
        setCouponValueType(event.target.value);
      }
    
    const CouponNameOnChange = (couponName) => {
        let errorObj = { ...errors };
        errorObj['couponName'] = '';
        setCouponName(couponName);
        setErrors(errorObj);
    }
    const couponValueOnChange = (couponValue) => {
        let errorObj = { ...errors };
        errorObj['couponValue'] = '';
        setCouponValue(couponValue);
        setErrors(errorObj);
    }
    const couponCodeOnChange = (couponCode) => {
        let errorObj = { ...errors };
        errorObj['couponCode'] = '';
        setCouponCode(couponCode);
        setErrors(errorObj);
    }
    const NoOfUsageOnChange = (noOfUsage) => {
            let errorObj = { ...errors };
            errorObj['noOfUsage'] = '';
            setNoOfUsage(noOfUsage);
            setErrors(errorObj);
        
    }
    const couponValueTypeOnChange = (couponValueType) => {
        let errorObj = { ...errors };
        errorObj['couponValueType'] = '';
        setCouponValueType(couponValueType);
        setErrors(errorObj);
    
}

const cartValueRangeTypeOnChange = (cartValueRangeType) => {
    let errorObj = { ...errors };
    errorObj['cartValueRangeType'] = '';
    setCartValueRangeType(cartValueRangeType);
    setErrors(errorObj);

}
const cartValueRangeOnChange = (cartValueRange) => {
    let errorObj = { ...errors };
    errorObj['cartValueRange'] = '';
    setCartValueRange(cartValueRange);
    setErrors(errorObj);

}

const NoOfUsagePerUserOnChange = (noOfUsageUser) => {
    let errorObj = { ...errors };
    errorObj['noOfUsageUser'] = '';
    setNoOfUsageUser(noOfUsageUser);
    setErrors(errorObj);

}
const ExpiryDateOnChange = (toDate) => {
    let errorObj = { ...errors };
    errorObj['toDate'] = '';
    setToDate(toDate);
    setErrors(errorObj);

}
const FromDateOnChange = (fromDate) => {
    let errorObj = { ...errors };
    errorObj['fromDate'] = '';
    setFromDate(fromDate);
    setErrors(errorObj);

}

    const saveOnClick = () => {
        if (couponId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        console.log("1245", "hi")
        if (couponName && couponCode && couponValue && noOfUsage && noOfUsageUser) {
            setLoader(true);
            let obj = {
                'coupon_name': couponName,
                'coupon_code': couponCode,
                'coupon_value': couponValue,
                'coupon_value_type': couponValueType,
                'cart_value_range': cartValueRange,
                'cart_value_range_type': cartValueRangeType,
                'cart_value': cartValue,
                'no_of_usage': noOfUsage,
                'no_of_usage_user': noOfUsageUser,
                'from_date': fromDate,
                'to_date': toDate,
            }
            try {
                await CouponRepository.saveCoupon(obj);
                notification.success({
                    message: 'Coupon  Added Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCoupon(ctr));
                dispatch(getInactiveCoupon(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Coupon  Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!couponName) errorObj['couponName'] = "Please Enter CouponName";
            if (!couponCode) errorObj['couponCode'] = "Please Enter couponCode";
            if (!couponValue) errorObj['couponValue'] = "Please Enter couponValue";
            if (!noOfUsage) errorObj['noOfUsage'] = "Please Enter noOfUsage";
            if (!noOfUsageUser) errorObj['noOfUsageUser'] = "Please Enter noOfUsageUser";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (couponName && couponCode && couponValue && noOfUsage && noOfUsageUser && couponId) {
            setLoader(true);
            let obj = {
                'coupon_id': couponId,
                'coupon_name': couponName,
                'coupon_code': couponCode,
                'coupon_value': couponValue,
                'coupon_value_type': couponValueType,
                'cart_value_range': cartValueRange,
                'cart_value_range_type': cartValueRangeType,
                'cart_value': cartValue,
                'no_of_usage': noOfUsage,
                'no_of_usage_user': noOfUsageUser,
                'from_date': fromDate,
                'to_date': toDate,


            }
            try {
                await CouponRepository.updateCoupon(couponId, obj);
                notification.success({
                    message: 'Dashboard Category  Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCoupon(ctr));
                dispatch(getInactiveCoupon(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Dashboard Category  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!couponName) errorObj['couponName'] = "Please Enter CouponName";
            if (!couponCode) errorObj['couponCode'] = "Please Enter couponCode";
            if (!couponValue) errorObj['couponValue'] = "Please Enter couponValue";
            if (!noOfUsage) errorObj['noOfUsage'] = "Please Enter noOfUsage";
            if (!noOfUsageUser) errorObj['noOfUsageUser'] = "Please Enter noOfUsageUser";
            setErrors(errorObj);
        }
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;
        if (tab === "active") {
            dispatch(getAllCoupon(ctr));
            ;
        } else {
            dispatch(getInactiveCoupon(ctr))
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllCoupon(ctr));
        } else {
            dispatch(getInactiveCoupon(ctr))
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allCoupon.map(h => h.coupon_id);
            } else {
                array = inactiveCoupon.map(h => h.coupon_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCouponId];
        let array1 = tab === 'active' ? [...allCoupon] : [...inactiveCoupon];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }


    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedCouponIdArr = [...selectedCouponId];
        let obj = {
            couponId: selectedCouponIdArr
        };
        if (selectedCouponIdArr && selectedCouponIdArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await CouponRepository.changeStatus(obj);
                notification.success({
                    message: 'Coupon  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await CouponRepository.changeStatus(obj);
                notification.success({
                    message: 'Coupon  Updated Successfully.'
                });
            }
            if (action === "delete") {
                obj['status'] = 'D';
                await CouponRepository.deleteCoupon(obj);
                notification.success({
                    message: 'Coupon  Deleted Successfully.'
                });
            }

            setSelectedHomeCatIds([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;
            dispatch(getAllCoupon(ctr));
            dispatch(getInactiveCoupon(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedCouponIdArr
                .length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Coupon'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>
                                </div>
                                <div style={{ marginTop: 20 }}>
                                    <TableCoupon
                                        allCoupon={tab === "active" ? allCoupon : inactiveCoupon}
                                        editModalOnClick={editModalOnClick}
                                        deleteOneDashboardCategory={deleteOneDashboardCategory}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCouponId={selectedCouponId}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={couponId ? "Edit Coupon " : "Add New Coupon "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label>Coupon Name <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponName}
                                        placeholder=""
                                        onChange={(e) => CouponNameOnChange(e.target.value)}

                                    />
                                    {errors['couponName'] &&
                                        <span style={{ color: 'red' }}>{errors['couponName']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>coupon Value <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponValue}
                                        placeholder=""
                                        onChange={(e) => couponValueOnChange(e.target.value)}

                                        
                                    />
                                    {errors['couponValue'] &&
                                        <span style={{ color: 'red' }}>{errors['couponValue']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Cart Value Range Type <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={cartValueRangeType}
                                        placeholder=""
                                        onChange={(e) => cartValueRangeTypeOnChange(e.target.value)}
                                    />
                                    {errors['cartValueRangeType'] &&
                                        <span style={{ color: 'red' }}>{errors['cartValueRangeType']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>No.of Usage <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={noOfUsage}
                                        placeholder=""
                                        onChange={(e) => NoOfUsageOnChange(e.target.value)}
                                       
                                    />
                                    {errors['noOfUsage'] &&
                                        <span style={{ color: 'red' }}>{errors['noOfUsage']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>from Date <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={fromDate}
                                        placeholder=""
                                        onChange={(e) => FromDateOnChange(e.target.value)}
                                    />
                                    {errors['fromDate'] &&
                                        <span style={{ color: 'red' }}>{errors['fromDate']}</span>
                                    }
                                </div>

                            </div>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label>CouponCode <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponCode}
                                        placeholder=""
                                        onChange={(e) => couponCodeOnChange(e.target.value)}

                                        
                                    />
                                    {errors['couponCode'] &&
                                        <span style={{ color: 'red' }}>{errors['couponCode']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Type <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        onChange={(event, value) => {
                                            handleTypeChange(event, value)
                                          }}
                                        value={couponValueType}
                                        value={"%"}
                                        value={"Rs"}
                                        placeholder=""
                                        onChange={(e) => couponValueTypeOnChange(e.target.value)}

                                        
                                    />
                                    {errors['couponValueType'] &&
                                        <span style={{ color: 'red' }}>{errors['couponValueType']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>CartValueRange <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={cartValueRange}
                                        placeholder=""
                                        onChange={(e) => cartValueRangeOnChange(e.target.value)}

                                        
                                    />
                                    {errors['cartValueRange'] &&
                                        <span style={{ color: 'red' }}>{errors['cartValueRange']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>No.of Usage Per User <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={noOfUsageUser}
                                        placeholder=""
                                        onChange={(e) => NoOfUsagePerUserOnChange(e.target.value)}

                                    />
                                    {errors['noOfUsageUser'] &&
                                        <span style={{ color: 'red' }}>{errors['noOfUsageUser']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Expiry Date <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={toDate}
                                        placeholder=""
                                        onChange={(e) => ExpiryDateOnChange(e.target.value)}

                                    />
                                    {errors['toDate'] &&
                                        <span style={{ color: 'red' }}>{errors['toDate']}</span>
                                    }
                                </div>

                            </div>
                        </div>
                        <div>
                        </div>
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
