import React, { useRef, useEffect, useState } from 'react';
import { Editor } from '@tinymce/tinymce-react';
import { decode, encode } from 'html-entities';
import { useDropzone } from "react-dropzone";
import { Spin, Modal } from 'antd';
import Router from 'next/router';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Button from '@material-ui/core/Button';

import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';

import { getQuery } from '../../../helper/auth';
import { questionImageDir, schoolquestionImageDir, TINY_EDITOR_KEY } from "../../../config";
import QuestionRepository from '../../../repositories/QuestionRepository';

const AddEditQuestion = (props) => {
    const questionEditorRef = useRef(null);
    const opt1EditorRef = useRef(null);
    const opt2EditorRef = useRef(null);
    const opt3EditorRef = useRef(null);
    const opt4EditorRef = useRef(null);
    const opt5EditorRef = useRef(null);

    const [loader, setLoader] = useState(false);
    const [question, setQuestion] = useState('');
    const [questionInit, setQuestionInit] = useState('');
    const [questionNo, setQuestionNo] = useState('');
    const [questionType, setQuestionType] = useState('T');
    const [questionId, setQuestionId] = useState('');
    const [questionDesc, setQuestionDesc] = useState('');
    const [option1Type, setOption1Type] = useState('T');
    const [option2Type, setOption2Type] = useState('T');
    const [option3Type, setOption3Type] = useState('T');
    const [option4Type, setOption4Type] = useState('T');
    const [option5Type, setOption5Type] = useState('T');
    const [difficultyLevel, setDifficultyLevel] = useState('');
    const [option1, setOption1] = useState('');
    const [option2, setOption2] = useState('');
    const [option3, setOption3] = useState('');
    const [option4, setOption4] = useState('');
    const [option5, setOption5] = useState('');
    const [option1Init, setOption1Init] = useState('');
    const [option2Init, setOption2Init] = useState('');
    const [option3Init, setOption3Init] = useState('');
    const [option4Init, setOption4Init] = useState('');
    const [option5Init, setOption5Init] = useState('');
    const [files, setFiles] = useState([]);
    const [opt1files, setOpt1Files] = useState([]);
    const [opt2files, setOpt2Files] = useState([]);
    const [opt3files, setOpt3Files] = useState([]);
    const [opt4files, setOpt4Files] = useState([]);
    const [opt5files, setOpt5Files] = useState([]);
    const [viewQuesImg, setviewQuesImg] = useState(false);
    const [viewQuesDropzone, setQuesDropzone] = useState(true);
    const [viewOpt1Img, setviewOpt1Img] = useState(false);
    const [viewOpt1Dropzone, setOpt1Dropzone] = useState(true);
    const [viewOpt2Img, setviewOpt2Img] = useState(false);
    const [viewOpt2Dropzone, setOpt2Dropzone] = useState(true);
    const [viewOpt3Img, setviewOpt3Img] = useState(false);
    const [viewOpt3Dropzone, setOpt3Dropzone] = useState(true);
    const [viewOpt4Img, setviewOpt4Img] = useState(false);
    const [viewOpt4Dropzone, setOpt4Dropzone] = useState(true);
    const [viewOpt5Img, setviewOpt5Img] = useState(false);
    const [viewOpt5Dropzone, setOpt5Dropzone] = useState(true);
    const [opt, setOpt] = useState('');
    const [correctAnswer, setCorrectAnswer] = useState('');
    const [screenmode, setMode] = useState("");
    const [ipaddr, setIpAddr] = useState('');
    const [categoryId, setCategoryId] = useState("");
    const [subCategoryId, setSubCategoryId] = useState("");
    const [categoryName, setCategoryName] = useState("");
    const [subCategoryName, setSubCategoryName] = useState("");

    const publicIp = require('public-ip');

    const thumbsContainer = {
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 16,
    };

    const thumb = {
        display: 'inline-flex',
        borderRadius: 2,
        border: '1px solid #eaeaea',
        marginBottom: 8,
        marginRight: 8,
        width: '100%',
        height: 150,
        padding: 4,
        boxSizing: 'border-box'
    };

    const thumbInner = {
        display: 'flex',
        minWidth: 0,
        overflow: 'hidden'
    };

    const img = {
        display: 'block',
        width: 'auto',
        height: '100%'
    };

    const handleRefresh = async () => {
        setLoader(true);
        let query = getQuery();
        let ip = await publicIp.v4();
        let mode = query && query.qid ? 'Edit' : 'Add';
        setMode(mode);
        setCategoryId(query.mainId);
        setSubCategoryId(query.subId);
        let mainName = query.mainName.replace(/%20/g, ' ');
        let subName = query.subName.replace(/%20/g, ' ');
        setCategoryName(mainName);
        setSubCategoryName(subName);
        setIpAddr(ip);
        if (mode !== 'Edit') {
            let getdata = {
                "cat_id": query.mainId,
                "sub_id": query.subId
            }
            let questionNoRes = await QuestionRepository.getQuestionId(getdata);
            setQuestionType('T');
            setQuestion('');
            setOption1Type('T');
            setOption2Type('T');
            setOption3Type('T');
            setOption4Type('T');
            setOption5Type('T');
            setOption1("");
            setOption2("");
            setOption3("");
            setOption4("");
            setOption5("");
            setQuestionNo(questionNoRes && questionNoRes.questionNo ? questionNoRes.questionNo : '');
        } else {
            let response = await QuestionRepository.getQuestionById(query.qid);
            let questiondatadetail = response.question;
            setQuestionNo(questiondatadetail.question_code)
            setQuestionId(questiondatadetail.qid);
            // let user = auth.getCurrentUser();
            let questionImageDirfinal = questionImageDir;
            // if (user.user.logintype != 'G') {
            //     questionImageDirfinal = schoolquestionImageDir;
            // }
            if (questiondatadetail.q_type === 'I') {
                let file = {
                    name: questiondatadetail.question,
                    preview: questionImageDirfinal + '/' + questiondatadetail.question
                }
                let filearray = [];
                filearray.push(file);
                setFiles(filearray);
                setQuesDropzone(false);
                setviewQuesImg(true);
            } else {
                let questionvalue = decode(questiondatadetail.question);
                setQuestionInit(questionvalue);
            }
            if (questiondatadetail.opt_type1 === 'I') {
                let opt1file = {
                    name: questiondatadetail.opt_1,
                    preview: questionImageDirfinal + '/' + questiondatadetail.opt_1
                }
                let opt1array = [];
                opt1array.push(opt1file);
                setOpt1Files(opt1array);
                setOpt1Dropzone(false);
                setviewOpt1Img(true);
            } else {
                let option1value = decode(questiondatadetail.opt_1);
                setOption1Init(option1value);
            }
            if (questiondatadetail.opt_type2 === 'I') {
                let opt2file = {
                    name: questiondatadetail.opt_2,
                    preview: questionImageDirfinal + '/' + questiondatadetail.opt_2
                }
                let opt2array = [];
                opt2array.push(opt2file);
                setOpt2Files(opt2array);
                setOpt2Dropzone(false);
                setviewOpt2Img(true);
            } else {
                let option2value = decode(questiondatadetail.opt_2);
                setOption2Init(option2value);
            }
            if (questiondatadetail.opt_type3 === 'I') {
                let opt3file = {
                    name: questiondatadetail.opt_3,
                    preview: questionImageDirfinal + '/' + questiondatadetail.opt_3
                }
                let opt3array = [];
                opt3array.push(opt3file);
                setOpt3Files(opt3array);
                setOpt3Dropzone(false);
                setviewOpt3Img(true);
            } else {
                let option3value = decode(questiondatadetail.opt_3);
                setOption3Init(option3value);
            }
            if (questiondatadetail.opt_type4 === 'I') {
                let opt4file = {
                    name: questiondatadetail.opt_4,
                    preview: questionImageDirfinal + '/' + questiondatadetail.opt_4
                }
                let opt4array = [];
                opt4array.push(opt4file);
                setOpt4Files(opt4array);
                setOpt4Dropzone(false);
                setviewOpt4Img(true);
            } else {
                let option4value = decode(questiondatadetail.opt_4);
                setOption4Init(option4value);
            }
            if (questiondatadetail.opt_type5 === 'I') {
                let opt5file = {
                    name: questiondatadetail.opt_5,
                    preview: questionImageDirfinal + '/' + questiondatadetail.opt_5
                }
                let opt5array = [];
                opt5array.push(opt5file);
                setOpt5Files(opt5array);
                setOpt5Dropzone(false);
                setviewOpt5Img(true);
            } else {
                let option5value = decode(questiondatadetail.opt_5);
                setOption5Init(option5value);
            }
            setQuestionDesc(questiondatadetail.quest_desc);
            setQuestionType(questiondatadetail.q_type);
            setOption1Type(questiondatadetail.opt_type1);
            setOption2Type(questiondatadetail.opt_type2);
            setOption3Type(questiondatadetail.opt_type3);
            setOption4Type(questiondatadetail.opt_type4);
            setOption5Type(questiondatadetail.opt_type5);
            setCorrectAnswer(questiondatadetail.crt_ans);
            setDifficultyLevel((questiondatadetail.quest_level).toString());
        }
        setLoader(false);
    }

    useEffect(() => {
        async function fetchData() {
            await handleRefresh()
        }
        fetchData();
    }, [])

    const handleQuestionType = (e, opt) => {
        setQuestionType(e.target.value);
        setOpt(opt);
    };

    const handleOptionType1 = (e, opt) => {
        setOption1Type(e.target.value);
        setOpt(opt);
    };

    const handleOptionType2 = (e, opt) => {
        setOption2Type(e.target.value);
        setOpt(opt);
    };

    const handleOptionType3 = (e, opt) => {
        setOption3Type(e.target.value);
        setOpt(opt);
    };

    const handleOptionType4 = (e, opt) => {
        setOption4Type(e.target.value);
        setOpt(opt);
    };

    const handleOptionType5 = (e, opt) => {
        setOption5Type(e.target.value);
        setOpt(opt);
    };

    const handleDifficultyLevel = (e) => {
        setDifficultyLevel(e.target.value);
    };

    const onCorrectAnsChange = (e) => {
        const re = /^[0-9\b]+$/;
        if (re.test(e.target.value) && Number(e.target.value) <= 5) {
            setCorrectAnswer(e.target.value);
        } else if (e.target.value === '') {
            setCorrectAnswer('');
        }
    };

    const onDescriptionchange = (e) => {
        setQuestionDesc(e.target.value);
    };

    const { getRootProps, getInputProps } = useDropzone({
        accept: 'image/*',
        onDrop: acceptedFiles => {
            if (opt === 'question') {
                setFiles(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewQuesImg(true);
                setQuesDropzone(false);
            }
            if (opt === 'opt1') {
                console.log(acceptedFiles);
                setOpt1Files(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewOpt1Img(true);
                setOpt1Dropzone(false);
            }
            if (opt === 'opt2') {
                setOpt2Files(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewOpt2Img(true);
                setOpt2Dropzone(false);
            }
            if (opt === 'opt3') {
                setOpt3Files(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewOpt3Img(true);
                setOpt3Dropzone(false);
            }
            if (opt === 'opt4') {
                setOpt4Files(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewOpt4Img(true);
                setOpt4Dropzone(false);
            }
            if (opt === 'opt5') {
                setOpt5Files(acceptedFiles.map(file => Object.assign(file, {
                    preview: window.webkitURL.createObjectURL(file)
                })));
                setviewOpt5Img(true);
                setOpt5Dropzone(false);
            }
        }
    });

    const questionThumb = files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const opt1Thumb = opt1files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const opt2Thumb = opt2files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const opt3Thumb = opt3files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const opt4Thumb = opt4files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const opt5Thumb = opt5files.map(file => (
        <div style={thumb} key={file.name}>
            <div style={thumbInner}>
                <img alt={file.name}
                    src={file.preview}
                    style={img}
                />
            </div>
        </div>
    ));

    const deleteImage = (type) => {
        if (type === 'question') {
            setviewQuesImg(false);
            setQuesDropzone(true);
        }
        if (type === 'opt1') {
            setviewOpt1Img(false);
            setOpt1Dropzone(true);
        }
        if (type === 'opt2') {
            setviewOpt2Img(false);
            setOpt2Dropzone(true);
        }
        if (type === 'opt3') {
            setviewOpt3Img(false);
            setOpt3Dropzone(true);
        }
        if (type === 'opt4') {
            setviewOpt4Img(false);
            setOpt4Dropzone(true);
        }
        if (type === 'opt5') {
            setviewOpt5Img(false);
            setOpt5Dropzone(true);
        }
    }

    const saveQuestion = async () => {
        setLoader(true);
        try {
            const formdata = new FormData();
            formdata.append("cat_id", categoryId);
            formdata.append("sub_id", subCategoryId);
            formdata.append("q_type", questionType);
            if (questionType === 'I') {
                if (files[0] !== undefined) {
                    formdata.append("question", files[0]);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Question is required'
                    });
                    return false;
                }
            } else {
                if (question !== '') {
                    let questionvalue = encode(question);
                    formdata.append("question", questionvalue);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Question is required'
                    });
                    return false;
                }
            }
            formdata.append("quest_desc", questionDesc);
            formdata.append("opt_type1", option1Type);
            if (option1Type === 'I') {
                if (opt1files[0] !== undefined) {
                    formdata.append("opt_1", opt1files[0]);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 1 is required'
                    });
                    return false;
                }
            } else {
                if (option1 !== '') {
                    let opt1value = encode(option1);
                    formdata.append("opt_1", opt1value);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 1 is required'
                    });
                    return false;
                }
            }
            formdata.append("opt_type2", option2Type);
            if (option2Type === 'I') {
                if (opt2files[0] !== undefined) {
                    formdata.append("opt_2", opt2files[0]);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 2 is required'
                    });
                    return false;
                }
            } else {
                if (option2 !== '') {
                    let opt2value = encode(option2);
                    formdata.append("opt_2", opt2value);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 2 is required'
                    });
                    return false;
                }
            }
            formdata.append("opt_type3", option3Type);
            if (option3Type === 'I') {
                if (opt3files[0] !== undefined) {
                    formdata.append("opt_3", opt3files[0]);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 3 is required'
                    });
                    return false;
                }
            } else {
                if (option3 !== '') {
                    let opt3value = encode(option3);
                    formdata.append("opt_3", opt3value);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 3 is required'
                    });
                    return false;
                }
            }
            formdata.append("opt_type4", option4Type);
            if (option4Type === 'I') {
                if (opt4files[0] !== undefined) {
                    formdata.append("opt_4", opt4files[0]);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 4 is required'
                    });
                    return false;
                }
            } else {
                if (option4 !== '') {
                    let opt4value = encode(option4);
                    formdata.append("opt_4", opt4value);
                } else {
                    setLoader(false);
                    Modal.error({
                        title: 'Option 4 is required'
                    });
                    return false;
                }
            }
            formdata.append("opt_type5", option5Type);
            if (option5Type === 'I') {
                formdata.append("opt_5", opt5files[0]);
            } else {
                let opt5value = encode(option5);
                formdata.append("opt_5", opt5value);
            }
            if (correctAnswer !== '') {
                formdata.append("crt_ans", correctAnswer);
            } else {
                setLoader(false);
                Modal.error({
                    title: 'Correct answer is required'
                });
                return false;
            }
            if (difficultyLevel !== '') {
                formdata.append("quest_level", difficultyLevel);
            } else {
                setLoader(false);
                Modal.error({
                    title: 'Level is required'
                });
                return false;
            }
            formdata.append("quest_pos", "0");
            formdata.append("quest_ipaddr", ipaddr);
            if (screenmode !== 'Edit') {
                await QuestionRepository.saveQuestion(formdata);
                Modal.success({
                    title: 'Question Added Successfully'
                });
                Router.back();
            } else {
                await QuestionRepository.updateQuestion(questionId, formdata);
                Modal.success({
                    title: 'Question Updated Successfully'
                });
                Router.back();
            }
            setLoader(false);
        } catch (ex) {
            setLoader(false);
            Modal.error({
                title: 'Something went wrong. Please try later'
            });
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Q Bank Sub Category'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <div className="row">
                                    <div className="col-md-10">
                                        <h4 style={{ padding: '0.5%' }}>Add New Question for ({categoryName} - {subCategoryName})</h4>
                                    </div>
                                    <div className="col-md-2">
                                        <Button onClick={() => Router.back()} variant="contained" color="primary" className="jr-btn">
                                            <i className="zmdi zmdi-chevron-left zmdi-hc-fw" />
                                            <span>Back</span>
                                        </Button>
                                    </div>
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12">
                                    <div className="form-group">
                                        <h6>Question ID <span style={{ color: 'red' }}>*</span></h6>
                                        <input
                                            className="form-control"
                                            type="text"
                                            value={questionNo}
                                            placeholder=""
                                        />
                                    </div>
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12">
                                    <FormControl component="fieldset" required>
                                        <h6>Question Type<span style={{ color: 'red' }}>*</span></h6>
                                        <RadioGroup
                                            className="d-flex flex-row"
                                            aria-label="questiontype"
                                            name="questiontype"
                                            value={questionType}
                                            onChange={(e) => handleQuestionType(e, 'question')}
                                        >
                                            <FormControlLabel value="T" control={<Radio color="primary" />} label="Text" />
                                            <FormControlLabel value="I" control={<Radio color="primary" />} label="Image" />
                                        </RadioGroup>
                                    </FormControl>
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12">
                                    {questionType === 'T' &&
                                        <div>
                                            <h6>Question <span style={{ color: 'red' }}>*</span></h6>
                                            <Editor
                                                apiKey={TINY_EDITOR_KEY}
                                                onInit={(evt, editor) => questionEditorRef.current = editor}
                                                initialValue={questionInit}
                                                value={question}
                                                onEditorChange={(newText) => setQuestion(newText)}
                                                init={{
                                                    height: 500,
                                                    menubar: false,
                                                    plugins: 'print preview powerpaste casechange importcss tinydrive searchreplace autolink autosave save directionality advcode visualblocks visualchars fullscreen image link media mediaembed template codesample table charmap hr pagebreak nonbreaking anchor toc insertdatetime advlist lists checklist wordcount tinymcespellchecker a11ychecker textpattern noneditable help formatpainter permanentpen pageembed charmap  mentions quickbars linkchecker emoticons advtable export',
                                                    toolbar: 'undo redo | bold italic underline strikethrough code subscript superscript | fontselect fontsizeselect formatselect | alignleft aligncenter alignright alignjustify | outdent indent |  numlist bullist checklist | forecolor backcolor casechange permanentpen formatpainter removeformat | pagebreak | charmap emoticons | fullscreen  preview save print | insertfile image media table pageembed template link anchor codesample | a11ycheck ltr rtl | tiny_mce_wiris_formulaEditor tiny_mce_wiris_formulaEditorChemistry',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }',
                                                    toolbar_drawer: 'sliding',
                                                    external_plugins: { tiny_mce_wiris: 'https://www.wiris.net/demo/plugins/tiny_mce/plugin.js' },
                                                    file_picker_callback: function (cb, value, meta) {
                                                        var input = document.createElement('input');
                                                        input.setAttribute('type', 'file');
                                                        input.setAttribute('accept', 'image/*');
                                                        input.onchange = function () {
                                                            var file = this.files[0];
                                                            const formData = new FormData();
                                                            formData.append('image', file);
                                                            QuestionRepository.imageUpload(formData).then(imageUploadResult => {
                                                                if (imageUploadResult && imageUploadResult.data && imageUploadResult.data.statusCode === 200) {
                                                                    cb(imageUploadResult.data.data);
                                                                } else {
                                                                    cb();
                                                                }
                                                            }).catch(e => {
                                                                cb();
                                                            });
                                                        };
                                                        input.click();
                                                    },
                                                    images_upload_handler: function (blob, success, failure) {
                                                        var file = blob.blob();
                                                        if (file && file.name) {
                                                            const formData = new FormData();
                                                            formData.append('image', file);
                                                            QuestionRepository.imageUpload(formData).then(imageUploadResult => {
                                                                if (imageUploadResult && imageUploadResult.data && imageUploadResult.data.statusCode === 200) {
                                                                    success(imageUploadResult.data.data);
                                                                } else {
                                                                    failure();
                                                                }
                                                            }).catch(e => {
                                                                failure();
                                                            });
                                                        }
                                                    }
                                                }}
                                            />
                                        </div>
                                    }
                                    {questionType === 'I' &&
                                        <>
                                            <h6>Question Image<span style={{ color: 'red' }}>*</span></h6>
                                            <div className="dropzone-card">
                                                {viewQuesDropzone &&
                                                    <div className="dropzone">
                                                        <div className="dropzone-file-btn" style={{ width: '100%' }} {...getRootProps({
                                                            onClick: event => setOpt('question')
                                                        }
                                                        )}>
                                                            <input {...getInputProps()} />
                                                            <p>Drag 'n' drop some files here, or click to select files</p>
                                                        </div>
                                                    </div>
                                                }
                                                {viewQuesImg &&
                                                    <div>
                                                        <div className="dropzone-content" style={thumbsContainer}>
                                                            {screenmode === 'Edit' ? questionThumb : questionThumb}
                                                        </div>
                                                        <Button onClick={() => deleteImage('question')} variant="contained" className="jr-btn bg-danger text-white">
                                                            <i className="fas fa-trash" />
                                                        </Button>
                                                    </div>
                                                }
                                            </div>
                                        </>
                                    }
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12" style={{ marginTop: 15 }}>
                                    <h6>Question Description</h6>
                                    <textarea
                                        id="required" className="border-1 form-control chat-textarea"
                                        onChange={(e) => onDescriptionchange(e)}
                                        value={questionDesc}
                                        placeholder="Question Description"
                                    />
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12" style={{ marginTop: 15 }}>
                                    <FormControl component="fieldset" required>
                                        <h6>Option Type 1<span style={{ color: 'red' }}>*</span></h6>
                                        <RadioGroup
                                            className="d-flex flex-row"
                                            aria-label="optiontype1"
                                            name="optiontype1"
                                            value={option1Type}
                                            onChange={(e) => handleOptionType1(e, 'opt1')}
                                        >
                                            <FormControlLabel value="T" control={<Radio color="primary" />} label="Text" />
                                            <FormControlLabel value="I" control={<Radio color="primary" />} label="Image" />
                                        </RadioGroup>
                                    </FormControl>
                                </div>
                                <div className="col-lg-12 col-sm-6 col-12">
                                    {option1Type === 'T' &&
                                        <div>
                                            <h6>Option 1 <span style={{ color: 'red' }}>*</span></h6>
                                            <Editor
                                                apiKey={TINY_EDITOR_KEY}
                                                onInit={(evt, editor) => opt1EditorRef.current = editor}
                                                initialValue={option1Init}
                                                value={option1}
                                                onEditorChange={(newText) => setOption1(newText)}
                                                init={{
                                                    height: 500,
                                                    menubar: false,
                                                    plugins: 'print preview powerpaste casechange importcss tinydrive searchreplace autolink autosave save directionality advcode visualblocks visualchars fullscreen image link media mediaembed template codesample table charmap hr pagebreak nonbreaking anchor toc insertdatetime advlist lists checklist wordcount tinymcespellchecker a11ychecker textpattern noneditable help formatpainter permanentpen pageembed charmap  mentions quickbars linkchecker emoticons advtable export',
                                                    toolbar: 'undo redo | bold italic underline strikethrough code subscript superscript | fontselect fontsizeselect formatselect | alignleft aligncenter alignright alignjustify | outdent indent |  numlist bullist checklist | forecolor backcolor casechange permanentpen formatpainter removeformat | pagebreak | charmap emoticons | fullscreen  preview save print | insertfile image media table pageembed template link anchor codesample | a11ycheck ltr rtl | tiny_mce_wiris_formulaEditor tiny_mce_wiris_formulaEditorChemistry',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }',
                                                    toolbar_drawer: 'sliding',
                                                    external_plugins: { tiny_mce_wiris: 'https://www.wiris.net/demo/plugins/tiny_mce/plugin.js' },
                                                    file_picker_callback: function (cb, value, meta) {
                                                        var input = document.createElement('input');
                                                        input.setAttribute('type', 'file');
                                                        input.setAttribute('accept', 'image/*');
                                                        input.onchange = function () {
                                                            var file = this.files[0];
                                                            const formData = new FormData();
                                                            formData.append('image', file);
                                                            QuestionRepository.imageUpload(formData).then(imageUploadResult => {
                                                                if (imageUploadResult && imageUploadResult.data && imageUploadResult.data.statusCode === 200) {
                                                                    cb(imageUploadResult.data.data);
                                                                } else {
                                                                    cb();
                                                                }
                                                            }).catch(e => {
                                                                cb();
                                                            });
                                                        };
                                                        input.click();
                                                    },
