import React from 'react';
import { Checkbox, Table } from 'antd';
import Moment from "moment";

const TableAnnouncements= ({ allAnnouncements, editModalOnClick,deleteOneAnnouncements, onSelectAll, onSelectOne, selectAll, selectedHomeCatIds, mainPositionOnChange, posotionChangeCategorys }) => {
  const columns = [
  
    {
      title: 'Package Name',
      dataIndex: 'PackageName',
    },
    {
      title: 'Master Category',
      dataIndex: 'MasterCategory',
    },
    {
      title: 'Main Category',
      dataIndex: 'MainCategory',
    },
    {
      title: 'Sub Category',
      dataIndex: 'SubCategory',
    },
    {
      title: 'Selling Price (INR)',
      dataIndex: 'SellingPrice',
    },
    {
      title: 'Package Date	',
      dataIndex: 'PackageDate',
    },
  
    {
      title: 'Edit',
      dataIndex: 'edit',
    },
    {
      title: 'Delete',
      dataIndex: 'delete',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check'
    }
  ];
  
  let data = allAnnouncements.map(a => {
    let obj = {
      key: a.package_id,
      PackageName: a.package_name,
      MasterCategory:a.master_cat,
      MainCategory:a.maincategory,
      SubCategory:a.sub_cat,
      SellingPrice:a.selling_price,
      PackageDate:Moment(a.package_date).format('DD-MM-YYYY'),

     
      edit: (<i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>),
      delete : (<i className="fa fa-trash" onClick={() => deleteOneAnnouncements(a)} style={{cursor: 'pointer'}}></i>),

      check: (
        <Checkbox
          onClick={() => onSelectOne(a.package_id)}
          checked={selectedHomeCatIds.indexOf(a.package_id) >= 0}
        />
      )
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableAnnouncements;
