import React from 'react';
import { Checkbox, Table } from 'antd';
import Link from 'next/link';
import moment from 'moment';
import { decode } from 'html-entities';

import { questionImageDir } from '../../config';

const TableQuestions = ({
  questions, onSelectAll, onSelectOne, selectAll, selectedCatIds,
  mainCatId, subCatId, mainCatName, subCatName
}) => {
  const columns = [
    {
      title: 'Name',
      dataIndex: 'q_type',
      width: '10%',
    },
    {
      title: 'Notification Date',
      dataIndex: 'question_code',
      width: '10%',
    },
    {
      title: 'Examination Date',
      dataIndex: 'question',
      width: '30%',
    },
    {
      title: 'Position',
      dataIndex: 'quest_add_by',
      width: '10%',
    },
    {
      title: 'Edit',
      dataIndex: 'edit',
      align: 'center',
      width: '5%',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check',
      width: '2%',
    }
  ];

  let data = questions.map(a => {
    let url = '';
    if (a.q_type === "P") {
      url = `/qbanksubcategory/questions/addEditPassageQuestion?mainId=${mainCatId}&subId=${subCatId}&mainName=${mainCatName}&subName=${subCatName}&qid=${a.qid}`
    } else {
      url = `/qbanksubcategory/questions/addEditQuestion?mainId=${mainCatId}&subId=${subCatId}&mainName=${mainCatName}&subName=${subCatName}&qid=${a.qid}`;
    }
    let obj = {
      key: a.qid,
      quest_add_by: a.quest_add_by,
      quest_level: a.quest_level,
      question_code: a.question_code,
      q_type: a.q_type === "P" ? "Passage" : a.q_type === "T" ? "Text" : "Image",
      quest_date: moment(a.quest_date).format('DD-MM-YYYY'),
      check: (
        <Checkbox
          onClick={() => onSelectOne(a.qid)}
          checked={selectedCatIds.indexOf(a.qid) >= 0}
        />
      ),
      edit: (
        <Link href={url}>
          <a><i className="fas fa-pen" style={{ cursor: 'pointer' }}></i></a>
        </Link>
      ),
      examView: (
        <i className="fas fa-file" style={{ cursor: 'pointer' }}></i>
      ),
      view: (
        <i className="fas fa-file" style={{ cursor: 'pointer' }}></i>
      )
    }

    if (a.q_type === "T") {
      obj.question = <div dangerouslySetInnerHTML={{ __html: decode(a.question) }}></div>;
    }
    if (a.q_type === "I") {
      obj.question = <img src={questionImageDir + '/' + a.question} />;
    }
    if (a.q_type === "P") {
      obj.question = <div dangerouslySetInnerHTML={{ __html: decode(a.question) }}></div>;
    }

    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableQuestions;
