import { actionTypes } from './action';

export const initState = {
    totalActiveQuestionCount: 0,
    totalInactiveQuestionCount: 0,
    totalWaitingQuestionCount: 0,
    activeQuestions: [],
    inactiveQuestions: [],
    waitingQuestions: [],
    activeQuestionCount: 0,
    inactiveQuestionCount: 0,
    waitingQuestionCount: 0,
};

function reducer(state = initState, action) {
    switch (action.type) {
        case actionTypes.GET_ACTIVE_QUESTIONS_SUCCESS:
            return {
                ...state,
                ...{
                    totalActiveQuestionCount: action.payload && action.payload.count ? action.payload.count : 0,
                    activeQuestions: action.payload && action.payload.questions && action.payload.questions.length > 0 ? action.payload.questions : [],
                    activeQuestionCount: action.payload && action.payload.questionsCount ? action.payload.questionsCount : 0
                },
            };
        case actionTypes.GET_INACTIVE_QUESTIONS_SUCCESS:
            return {
                ...state,
                ...{
                    totalInactiveQuestionCount: action.payload && action.payload.count ? action.payload.count : 0,
                    inactiveQuestions: action.payload && action.payload.questions && action.payload.questions.length > 0 ? action.payload.questions : [],
                    inactiveQuestionCount: action.payload && action.payload.questionsCount ? action.payload.questionsCount : 0
                },
            };
        case actionTypes.GET_WAITING_QUESTIONS_SUCCESS:
            return {
                ...state,
                ...{
                    totalWaitingQuestionCount: action.payload && action.payload.count ? action.payload.count : 0,
                    waitingQuestions: action.payload && action.payload.questions && action.payload.questions.length > 0 ? action.payload.questions : [],
                    waitingQuestionCount: action.payload && action.payload.questionsCount ? action.payload.questionsCount : 0
                },
            };
        default:
            return state;
    }
}

export default reducer;
