import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableExampackage from '../../../components/tables/Exampackage';
import { getAllExampackage, getInactiveExampackage } from '../../../store/Exampackage/action';
import ExampackageRepository from '../../../repositories/ExampackageRepository';
import ExamMainCategoryRepository from '../../../repositories/ExamMainCategoryRepository';


const Home = (props) => {
    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const { allExampackage, totalActiveCount, inactiveExampackage, totalInactiveCount } = useSelector(({ Exampackage }) => Exampackage);
    const [showModal, setShowModal] = useState(false);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [action, setAction] = useState('');
    const [search, setsearch] = useState('');
    const [position, setPosition] = useState('');
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [packagename, setPackageName] = useState('');
    const [maincategoryId, setMainCategoryId] = useState('');
    const [subcategoryId, setSubCategoryId] = useState('');
    const [subsubcategoryId, setSubSubCategoryId] = useState('');
    const [chapterId, setChapterId] = useState('');
    const [offerprice, setOfferPrice] = useState('0');
    const [selectedHomeCatIds, setSelectedHomeCatIds] = useState([]);
    const [mastercategory, setmastercategory] = useState([]);
    const [subcategory, setsubcategory] = useState([]);
    const [maincategory, setmaincategory] = useState([]);
    const [durationId, setdurationId] = useState('');
    const [durationitems, setdurationitems] = useState('');
    const [durationRow, setDurationRow] = useState([{ rowsNo: 1, durationId: '', price: '', addtype: true, deltype: false }
    ]);
    const [sellingPrice, setsellingPrice] = useState('');
    const [Price, setPrice] = useState('');


    const renderDurations = () => {
        let rowNum = 1;
        return durationRow.map((row, index) => {
            rowNum = rowNum + 1;
            const { durationId, price, rowsNo, addtype, deltype } = row //destructuring
            return (
                <div className="row">
                    <div
                        margin="normal"
                        style={{ marginLeft: "15px", marginRight: "15px" }}
                        className="col-lg-3 col-sm-6 col-12"
                    >
                        {/* <label htmlFor="age-simple">Duration</label> */}
                        {/* <Select
                  onChange={(event, value) => {
                    handleDurationChange(event, index);
                  }}
                  value={durationId}
                >
                  {durationitems}
                </Select> */}
                        <div className="form-group">

                            <label> Duration<span style={{ color: 'red' }}>*</span></label>
                            <input
                                className="form-control"
                                type="text"
                                value={chapterId}
                                placeholder=""
                                onChange={(e) => handleDurationChange(e.target.value)}
                            />
                            {errors['chapterId'] &&
                                <span style={{ color: 'red' }}>{errors['chapterId']}</span>
                            }
                        </div>

                    </div>
                    <div className="form-group">
                        <label> Price<span style={{ color: 'red' }}>*</span></label>
                        <input
                            className="form-control"
                            type="text"
                            value={sellingPrice}
                            placeholder=""
                            onChange={(e) => onPriceChange(e, index)}
                        />
                        {errors['sellingPrice'] &&
                            <span style={{ color: 'red' }}>{errors['sellingPrice']}</span>
                        }
                    </div>
                    {addtype ? (

                        <button onClick={() => addDuration(rowNum)} className="icon-btn">
                            <i className="fa fa-plus" />
                        </button>

                    ) : (
                        <button onClick={() => removeDuration(rowsNo)} className="icon-btn">
                            <i  style={{ color: 'red' }} className="fa fa-trash" />
                        </button>

                    )}
                </div>
            )
        })
    }

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllExampackage(ctr));
        dispatch(getInactiveExampackage(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allExampackage, inactiveExampackage]);

    const onPriceChange = (e, index) => {
        let newArr = [...durationRow];
        newArr[index].price = e.target.value;
        setDurationRow(newArr);
    }

    const handleDurationChange = async (event, index) => {
        let newArr = [...durationRow];
        newArr[index].durationId = event.target.value;
        setDurationRow(newArr);
    };

    const addModalOnClick = async () => {
        setLoader(true);
        let result = await ExamMainCategoryRepository.getExamMainCategory();
        if (result && result.category && result.category.length > 0) {
            setmastercategory(result.category);
        }
        setSelectedHomeCatId('');
        setLoader(false);
        setShowModal(true);
    }

    const addDuration = (data) => {

        let nextRow = { rowsNo: data, durationId: '', price: '', addtype: false, deltype: true };
        setDurationRow(state => [...state, nextRow])
        console.log(nextRow, "asdf")
    }

    const removeDuration = (data) => {
        debugger
        durationRow.filter(item => item.rowsNo !== data)
        setDurationRow(durationRow.filter(item => item.rowsNo !== data));
        console.log(data, "asdf123")
    }




    const editModalOnClick = async (data) => {
        console.log(data, "data")
        setLoader(true);
        let result = await ExamMainCategoryRepository.getExammainCategoryById(data.sub_cat);
        if (result && result.category && result.category.length > 0) {
            console.log(result, "result.category")
            setmastercategory(result.category);
        }

        setdurationId(data.duration_ref_id)
        setPackageName(data.package_name)
        setMainCategoryId(data.main_cat)
        setsellingPrice(data.selling_price)
        setChapterId(data.chapt_id)
        setmastercategory(data.mastercategory)
        setmaincategory(data.maincategory)
        setsubcategory(data.subcategory)
        setsellingPrice(data.selling_price)
        setdurationitems()
        setLoader(false);
        setShowModal(true);
    }

    const handleMainCategoryChange = async (event, value) => {
        console.log(event.target.value, "11111111");
        setMainCategoryId(event.target.value);
        const { data: subcategoryres } = await ExamMainCategoryRepository.getExamSubCategoryById(event.target.value);
        const { category: categories } = subcategoryres;
        let itemArr = [];
        for (let subcategory of categories) {
            itemArr.push(value = subcategory.exa_cat_id && subcategory.exa_cat_name)
        }
        setSubCategoryItems(itemArr);
    }

    const closeModalOnClick = () => {
        setSelectedHomeCatId('');
        setErrors({});
        setShowModal(false);
    }

    const exampackageOnChange = (packagename) => {
        let errorObj = { ...errors };
        errorObj['packagename'] = '';
        setPackageName(packagename);
        setErrors(errorObj);
    }

    const MasterCategoryOnChange = (mastercategory) => {
        let errorObj = { ...errors };
        errorObj['mastercategory'] = '';
        setmastercategory(mastercategory);
        setErrors(errorObj);
    }

    const MainCategoryOnChange = (maincategory) => {
        let errorObj = { ...errors };
        errorObj['maincategory'] = '';
        setmaincategory(maincategory);
        setErrors(errorObj);
    }

    const SubCategoryOnChange = (subcategory) => {
        let errorObj = { ...errors };
        errorObj['subcategory'] = '';
        setsubcategory(subcategory);
        setErrors(errorObj);
    }

    const ChaptersOnChange = (chapterId) => {
        let errorObj = { ...errors };
        errorObj['chapterId'] = '';
        setChapterId(chapterId);
        setErrors(errorObj);
    }

    const SellingPriceOnChange = (sellingPrice) => {
        let errorObj = { ...errors };
        errorObj['sellingPrice'] = '';
        setsellingPrice(sellingPrice);
        setErrors(errorObj);
    }
    const saveOnClick = () => {
        if (selectedHomeCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (packagename && maincategoryId) {
            for (let duration of durationRow) {
                if (duration.durationId != "") {
                    durArr.push(duration.durationId);
                }
                if (duration.price != "") {
                    durArr.push(duration.price);
                }
            }
            if (durArr.length > 0) {
                setLoader(true);
                let obj = {
                    "package_name": packagename,
                    "master_cat": maincategoryId,
                    "main_cat": subcategoryId,
                    "sub_cat": subsubcategoryId,
                    "chapt_id": chapterId,
                    "selling_price": sellingPrice,
                    //"offer_price : offerprice,
                    "duration": durationRow
                }
                try {
                    await ExampackageRepository.saveExampackage(obj);
                    notification.success({
                        message: 'Exampackage Added Successfully.'
                    })
                    let ctr = {};
                    ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                    ctr._limit = pageSizeTotal;
                    ctr.search = search;

                    dispatch(getAllExampackage(ctr));
                    dispatch(getInactiveExampackage(ctr));
                    closeModalOnClick();
                } catch (e) {
                    notification.error({
                        message: 'Exampackage Added Failed.'
                    });
                }
            } else {
                let errorObj = { ...errors };
                if (!packagename) errorObj['packagename'] = "Please Enter packagename";
                setErrors(errorObj);
            }
        } else {
            let errorObj = { ...errors };
            if (!packagename) errorObj['packagename'] = "Please Enter packagename";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (selectedHomeCatId) {
            setLoader(true);
            let obj = {
                "package_id": selectedHomeCatId,
                "package_name": packagename,
                "master_cat": maincategoryId,
                "main_cat": subcategoryId,
                "sub_cat": subsubcategoryId,
                "chapt_id": chapterId,
                "selling_price": sellingPrice,
                "duration": durationRow
            }
            try {
                await ExampackageRepository.editExampackage(selectedHomeCatId, obj);
                notification.success({
                    message: 'Exampackage Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllExampackage(ctr));
                dispatch(getInactiveExampackage(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Exampackage  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!packagename) errorObj['packagename'] = "Please Enter packagename";
            setErrors(errorObj);
        }
    }
    const searchOnChange = (search) => {

        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.search = search;
        if (tab === 'active') {
            dispatch(getAllExampackage(ctr));
        } else {
            dispatch(getInactiveExampackage(ctr));
        }
        setsearch(search);
        setCurrentPage(1);
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;

        if (search) {
            ctr.search = search;
        }
        if (tab === "active") {
            dispatch(getAllExampackage(ctr));
        } else {
            dispatch(getInactiveExampackage(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllExampackage(ctr));
        } else {
            dispatch(getInactiveExampackage(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
        setsearch('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allExampackage.map(h => h.package_id);
            } else {
                array = inactiveExampackage.map(h => h.package_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedHomeCatIds];
        let array1 = tab === 'active' ? [...allExampackage] : [...inactiveExampackage];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }



    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedHomeCatIds];
        let obj = {
            packageId: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await ExampackageRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Exampackage  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await ExampackageRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Exampackage  Updated Successfully.'
                });
            }
            if (action === "delete") {
                obj['status'] = 'D';
                await ExampackageRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Exampackage  Deleted Successfully.'
                });
            }

            setSelectedHomeCatIds([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;

            if (search) {
                ctr.search = search;
            }
            dispatch(getAllExampackage(ctr));
            dispatch(getInactiveExampackage(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Exampackage'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>

                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group ">
                                            <input
                                                className="form-control"
                                                type="text"
                                                value={search}
                                                placeholder="search"
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 20 }}>
                                    <TableExampackage
                                        allExampackage={tab === "active" ? allExampackage : inactiveExampackage}
                                        editModalOnClick={editModalOnClick}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedHomeCatIds={selectedHomeCatIds}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedHomeCatId ? "Edit Exampackage " : "Add New Exampackage "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Package Name <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={packagename}
                                        placeholder=""
                                        onChange={(e) => exampackageOnChange(e.target.value)}
                                    />
                                    {errors['packagename'] &&
                                        <span style={{ color: 'red' }}>{errors['packagename']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Master Category <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={mastercategory}
                                        placeholder=""
                                        onChange={(e) => MasterCategoryOnChange(e.target.value)}
                                    />
                                    {errors['mastercategory'] &&
                                        <span style={{ color: 'red' }}>{errors['mastercategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label> Main Category <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="submit"
                                        placeholder=""
                                        value={maincategory}
                                        onChange={(e) => MainCategoryOnChange(e.target.value)}
                                    />
                                    {errors['maincategory'] &&
                                        <span style={{ color: 'red' }}>{errors['maincategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>  Sub Category<span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={subcategory}
                                        placeholder=""
                                        onChange={(e) => SubCategoryOnChange(e.target.value)}
                                    />
                                    {errors['subcategory'] &&
                                        <span style={{ color: 'red' }}>{errors['subcategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>  Chapters<span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={chapterId}
                                        placeholder=""
                                        onChange={(e) => ChaptersOnChange(e.target.value)}
                                    />
                                    {errors['chapterId'] &&
                                        <span style={{ color: 'red' }}>{errors['chapterId']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label> Selling Price<span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={sellingPrice}
                                        placeholder=""
                                        onChange={(e) => SellingPriceOnChange(e.target.value)}
                                    />
                                    {errors['sellingPrice'] &&
                                        <span style={{ color: 'red' }}>{errors['sellingPrice']}</span>
                                    }
                                </div>
                            </div>
                        </div>
                        <div className='row'>
                            <div className="col-md-12">
                                <h3> Subscriptions</h3>
                            </div>
                        </div>
                        {renderDurations()}
                        {/* <div className='row'>
                            
                            <div className="col-md-6">
                            <div className="form-group">
                      
                            <label> Duration<span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={chapterId}
                                        placeholder=""
                                        onChange={(e) =>   handleDurationChange(e.target.value)}
                                   />
                                    {errors['chapterId'] &&
                                        <span style={{ color: 'red' }}>{errors['chapterId']}</span>
                                    }
                        </div>
                        </div>
                        <div className="col-md-6">
                        <div className="form-group">
                      
                      <label> Price<span style={{ color: 'red' }}>*</span></label>
                              <input 
                                  className="form-control"
                                  type="text"
                                  value={sellingPrice}
                                  placeholder=""
                                  onChange={(e) =>   onPriceChange(e.target.value)}
                             />
                              {errors['sellingPrice'] &&
                                  <span style={{ color: 'red' }}>{errors['sellingPrice']}</span>
                              }
                  </div>
                  </div>
            <button onClick={() => addDuration()} className="icon-btn">
              <i className="fa fa-plus" />
            </button>
        
              <button onClick={() => removeDuration()} className="icon-btn">
                <i className="fa fa-trash" />
              </button>
         
                       
                    </div> */}


                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
