import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableExampackage from '../../../components/tables/Exampackage';
import { getAllAnnouncements, getInactiveAnnouncements } from '../../../store/Exampackage/action';
import ExampackageRepository from '../../../repositories/ExampackageRepository';
import ExamMainCategoryRepository from '../../../repositories/ExamMainCategoryRepository';


const Home = (props) => {
    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const { allAnnouncements, totalActiveCount, inactiveAnnouncements, totalInactiveCount } = useSelector(({ Exampackage }) => Exampackage);
    const [showModal, setShowModal] = useState(false);
    const [position, setPosition] = useState('');
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [packagename, setPackageName] = useState('');
    const [maincategoryId, setMainCategoryId] = useState('');
    const [subcategoryId, setSubCategoryId] = useState('');
    const [subsubcategoryId, setSubSubCategoryId] = useState('');
    const [chapterId, setChapterId] = useState('');
    const [sellingprice, setSellingPrice] = useState('0');
    const [offerprice, setOfferPrice] = useState('0');
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedHomeCatIds, setSelectedHomeCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [Announcements, setAnnouncements] = useState('');
    const [search, setsearch] = useState('');
    const [posotionChangeCategorys, setPosotionChangeCategorys] = useState([]);
    const [mastercategory,setmastercategory] = useState([]);
    const [subcategory,setsubcategory]= useState([]);
    const [maincategory,setmaincategory]= useState([]);
    const [durationId,setdurationId]= useState('');
    const [durationitems,setdurationitems]= useState('');
    const [durationRow, setDurationRow] = useState([]);
    const [sellingPrice,setsellingPrice]= useState('');
   
   
    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllAnnouncements(ctr));
        dispatch(getInactiveAnnouncements(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allAnnouncements, inactiveAnnouncements]);

    const onPriceChange = (e, index) => {
        let newArr = [...durationRow]; 
        newArr[index].price = e.target.value; 
        setDurationRow(newArr); 
      }

      const handleDurationChange = async (event, index) => {
        let newArr = [...durationRow]; 
        newArr[index].durationId = event.target.value;
        setDurationRow(newArr);
      };

    const addModalOnClick = async () => {
        setLoader(true);
        setPosition('');
        setSelectedHomeCatId('');
        setLoader(false);
        setShowModal(true);
    }

    const addDuration = (index) => {
        let nextRow = { rowsNo: index, durationId: '', price: '', addtype: false, deltype: true };
        setDurationRow(state => [...state, nextRow])
      }
    
      const removeDuration = (index) => {
        setDurationRow(durationRow.filter(item => item.rowsNo !== index));
      }
    const deleteOneAnnouncements = (data) => {
        Modal.confirm({
            title: 'Confirm',
            content: 'Do you want to delete this Announcements.',
            okText: 'Yes',
            onOk: () => deleteOneCategory(data),
            cancelText: 'No',
        });
    }

    const deleteOneCategory = async (data) => {
        let obj = { "AnnouncementsId": [data.Announcements_id] };
        await ExampackageRepository.deleteCategory(obj);
        Modal.success({
            title: 'Announcements Deleted Successfully.'
        });
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllAnnouncements(ctr));
        dispatch(getInactiveAnnouncements(ctr));
    }

    const editModalOnClick = async (data) => {
        console.log(data,"123456r")
        setLoader(true);
        setdurationId(data.duration_ref_id)
        setPackageName(data.package_name)
        setMainCategoryId(data.main_cat)
        setSellingPrice(data.selling_price)
        setChapterId(data.chapt_id)
        setmastercategory(data.mastercategory)
        setmaincategory(data.maincategory)
        setsubcategory(data.subcategory)
        setsellingPrice(data.selling_price)
        setdurationitems()
        setLoader(false);
        setShowModal(true);
    }

    const handleMainCategoryChange = async (event, value) => {
        console.log(event.target.value);
        setMainCategoryId(event.target.value);
        const { data: subcategoryres } = await ExamMainCategoryRepository.getExamSubCategoryById(event.target.value);
        const { category: categories } = subcategoryres;
        let itemArr = [];
        for (let subcategory of categories) {
          itemArr.push(<MenuItem value={subcategory.exa_cat_id}>{subcategory.exa_cat_name}</MenuItem>)
        }
        setSubCategoryItems(itemArr);
      }

    const closeModalOnClick = () => {
        setPosition('');
        setSelectedHomeCatId('');
        setAnnouncements('');
        setErrors({});
        setShowModal(false);
    }

    const exampackageOnChange = (packagename) => {
        let errorObj = { ...errors };
        errorObj['packagename'] = '';
        setPackageName(packagename);
        setErrors(errorObj);
    }
    
    const MasterCategoryOnChange = (mastercategory) => {
        let errorObj = { ...errors };
        errorObj['mastercategory'] = '';
        setmastercategory(mastercategory);
        setErrors(errorObj);
    }
    
    const MainCategoryOnChange = (maincategory) => {
        let errorObj = { ...errors };
        errorObj['maincategory'] = '';
        setmaincategory(maincategory);
        setErrors(errorObj);
    }
    
    const SubCategoryOnChange = (subcategory) => {
        let errorObj = { ...errors };
        errorObj['subcategory'] = '';
        setsubcategory(subcategory);
        setErrors(errorObj);
    }
    
    const ChaptersOnChange = (Announcements) => {
        let errorObj = { ...errors };
        errorObj['Announcements'] = '';
        setAnnouncements(Announcements);
        setErrors(errorObj);
    }
    
    const SellingPriceOnChange = (Announcements) => {
        let errorObj = { ...errors };
        errorObj['Announcements'] = '';
        setAnnouncements(Announcements);
        setErrors(errorObj);
    }
    const saveOnClick = () => {
        if (selectedHomeCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (packagename && maincategoryId) {
        for (let duration of durationRow) {
            if (duration.durationId != "") {
              durArr.push(duration.durationId);
            }
            if (duration.price != "") {
              durArr.push(duration.price);
            }
          }
        if (durArr.length > 0) {
            setLoader(true);
            let obj = {
                "package_name" : packagename,
                "master_cat" : maincategoryId,
                "main_cat" : subcategoryId,
                "sub_cat" : subsubcategoryId,
                "chapt_id" : chapterId,
                "selling_price" : sellingprice,
                //"offer_price : offerprice,
                "duration" : durationRow
            }
            try {
                await ExampackageRepository.saveAnnouncements(obj);
                notification.success({
                    message: 'Announcements Added Successfully.'
                })
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                ctr.search = search;
               
                dispatch(getAllAnnouncements(ctr));
                dispatch(getInactiveAnnouncements(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Announcements Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!packagename) errorObj['packagename'] = "Please Enter packagename";
            setErrors(errorObj);
        }
    } else {
        let errorObj = { ...errors };
        if (!packagename) errorObj['Announcements'] = "Please Enter Title";
        if (!maincategoryId) errorObj['position'] = "Please Enter Position";
        setErrors(errorObj);
    }
    }

    const edit = async () => {
        if (position && selectedHomeCatId && Announcements) {
            setLoader(true);
            let obj = {
                "Announcements_id": selectedHomeCatId,
                "Announcements_pos": position,
                "Announcements": Announcements
            }
            try {
                await ExampackageRepository.editAnnouncements(selectedHomeCatId, obj);
                notification.success({
                    message: 'Announcements Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllAnnouncements(ctr));
                dispatch(getInactiveAnnouncements(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Announcements  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!Announcements) errorObj['Announcements'] = "Please Enter Title";
            if (!position) errorObj['position'] = "Please Enter Position";
            setErrors(errorObj);
        }
    }
    const searchOnChange = (search) => {
      
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.search = search;
        if (tab === 'active') {
            dispatch(getAllAnnouncements(ctr));
        } else {
            dispatch(getInactiveAnnouncements(ctr));
        }
        setsearch(search);
        setCurrentPage(1);
    }
   
    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;

        if (search) {
            ctr.search = search;
        }
        if (tab === "active") {
            dispatch(getAllAnnouncements(ctr));
        } else {
            dispatch(getInactiveAnnouncements(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllAnnouncements(ctr));
        } else {
            dispatch(getInactiveAnnouncements(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
        setsearch('');
        setPosotionChangeCategorys([]);
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allAnnouncements.map(h => h.Announcements_id);
            } else {
                array = inactiveAnnouncements.map(h => h.Announcements_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedHomeCatIds];
        let array1 = tab === 'active' ? [...allAnnouncements] : [...inactiveAnnouncements];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }

    const mainPositionOnChange = (id, position) => {
        let array = [...posotionChangeCategorys];
        const re = /^[0-9\b]+$/; //rules
        if (position === "" || re.test(position)) {
            let index = array.findIndex(a => a.AnnouncementsId === id);
            if (index >= 0) {
                array[index]['position'] = position;
            } else {
                array.push({
                    AnnouncementsId: id,
                    position: position
                });
            }
            setPosotionChangeCategorys(array);
        }
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedHomeCatIds];
        let obj = {
            AnnouncementsId: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await ExampackageRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Announcements  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await ExampackageRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Announcements  Updated Successfully.'
                });
            }
            if (action === "delete") {
                await ExampackageRepository.deleteCategory(obj);
                notification.success({
                    message: 'Announcements  Deleted Successfully.'
                });
            }
            if (action === "position") {
                let array = [...posotionChangeCategorys];
                array = array.filter(a => selectedHomeCatIdsArr.indexOf(a.AnnouncementsId) >= 0);
                await ExampackageRepository.changePosition({ values: array });
                notification.success({
                    message: 'Announcements  Updated Successfully.'
                });
            }
            setSelectedHomeCatIds([]);
            setPosotionChangeCategorys([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;

            if (search) {
                ctr.search = search;
            }
            dispatch(getAllAnnouncements(ctr));
            dispatch(getInactiveAnnouncements(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Announcements'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="position">Position</Option>
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>

                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group ">
                                            <input
                                                className="form-control"
                                                type="text"
                                                value={search}
                                                placeholder="search"
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 20 }}>
                                    <TableExampackage
                                        allAnnouncements={tab === "active" ? allAnnouncements : inactiveAnnouncements}
                                        editModalOnClick={editModalOnClick}
                                        deleteOneAnnouncements={deleteOneAnnouncements}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedHomeCatIds={selectedHomeCatIds}
                                        mainPositionOnChange={mainPositionOnChange}
                                        posotionChangeCategorys={posotionChangeCategorys}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedHomeCatId ? "Edit Announcements " : "Add New Announcements "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Package Name <span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={packagename}
                                        placeholder=""
                                        onChange={(e) => exampackageOnChange(e.target.value)}
                                   />
                                    {errors['packagename'] &&
                                        <span style={{ color: 'red' }}>{errors['packagename']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Master Category <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        onChange={(event, value) => {
                                            handleMainCategoryChange(event, value)
                                          }} value={maincategoryId}
                                        placeholder=""
                                      //  onChange={(e) => MasterCategoryOnChange(e.target.value)}
                                   />
                                    {errors['mastercategory'] &&
                                        <span style={{ color: 'red' }}>{errors['mastercategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label> Main Category <span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={maincategory}
                                        placeholder=""
                                        onChange={(e) =>  MainCategoryOnChange(e.target.value)}
                                   />
                                    {errors['maincategory'] &&
                                        <span style={{ color: 'red' }}>{errors['maincategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>  Sub Category<span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={subcategory}
                                        placeholder=""
                                        onChange={(e) =>  SubCategoryOnChange(e.target.value)}
                                   />
                                    {errors['subcategory'] &&
                                        <span style={{ color: 'red' }}>{errors['subcategory']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>  Chapters<span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={Announcements}
                                        placeholder=""
                                        onChange={(e) => ChaptersOnChange(e.target.value)}
                                   />
                                    {errors['Announcements'] &&
                                        <span style={{ color: 'red' }}>{errors['Announcements']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label> Selling Price<span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={sellingPrice}
                                        placeholder=""
                                        onChange={(e) =>   SellingPriceOnChange(e.target.value)}
                                   />
                                    {errors['sellingPrice'] &&
                                        <span style={{ color: 'red' }}>{errors['sellingPrice']}</span>
                                    }
                                </div>
                            </div>
                            <div className='row'>
                            <div className="col-md-12">
                            <h3> Subscriptions</h3>
                            <div className="form-group">
                      
                            <label> Duration<span style={{ color: 'red' }}>*</span></label>
                                    <input 
                                        className="form-control"
                                        type="text"
                                        value={Announcements}
                                        placeholder=""
                                        onChange={(e) =>   handleDurationChange(e.target.value)}
                                   />
                                    {errors['Announcements'] &&
                                        <span style={{ color: 'red' }}>{errors['Announcements']}</span>
                                    }
                        </div>
                        <div className="form-group">
                      
                      <label> Price<span style={{ color: 'red' }}>*</span></label>
                              <input 
                                  className="form-control"
                                  type="text"
                                  value={sellingPrice}
                                  placeholder=""
                                  onChange={(e) =>   onPriceChange(e.target.value)}
                             />
                              {errors['sellingPrice'] &&
                                  <span style={{ color: 'red' }}>{errors['sellingPrice']}</span>
                              }
                  </div>
                  {selectedHomeCatId ? (
            <button onClick={() => addDuration(rowNum)} className="icon-btn">
              <i className="zmdi zmdi-plus zmdi-hc-fw" />
            </button>
          ) : (
              <button onClick={() => removeDuration(rowsNo)} className="icon-btn">
                <i className="zmdi zmdi-delete zmdi-hc-fw" />
              </button>
            )}
                        </div>
                    </div>
       
        </div>
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
