import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllAnnouncementsSuccess, getInactiveAnnouncementsSuccess } from './action';

import ExampackageRepository from '../../repositories/ExampackageRepository';

function* getAllAnnouncementsSaga({ payload }) {
    try {
        const data = yield call(ExampackageRepository.getAllAnnouncements, payload);
        yield put(getAllAnnouncementsSuccess(data));
    } catch (err) {
        yield put(getAllAnnouncementsSuccess(null));
    }
}

function* getInactiveAnnouncementsSaga({ payload }) {
    try {
        const data = yield call(ExampackageRepository.getInactiveAnnouncements, payload);
        yield put(getInactiveAnnouncementsSuccess(data));
    } catch (err) {
        yield put(getInactiveAnnouncementsSuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_EXAMPACKAGE_REQUEST, getAllAnnouncementsSaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_EXAMPACKAGE_REQUEST, getInactiveAnnouncementsSaga)]);
}
