import React from 'react';
import { Checkbox, Table } from 'antd';

const TableHomeCategory = ({ allHomeCategory, editModalOnClick, onSelectAll, onSelectOne, selectAll, selectedHomeCatIds, mainPositionOnChange, posotionChangeCategorys }) => {
  const columns = [
    {
      title: 'Category',
      dataIndex: 'category',
    },
    {
      title: 'Position',
      dataIndex: 'position',
    },
    {
      title: 'Edit',
      dataIndex: 'edit',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check'
    }
  ];

  let data = allHomeCategory.map(a => {
    let position = posotionChangeCategorys.filter(p => p.catId === a.exa_cat_id);
    let obj = {
      key: a.exa_cat_id,
      category: a.exa_cat_name,
      position: (
        <input
          type="text"
          onChange={event => mainPositionOnChange(a.exa_cat_id, event.target.value)}
          style={{ width: '50px', textAlign: 'center', padding: '0px' }}
          value={position && position.length > 0 ? position[0].position : a.exa_cat_pos}
          className="form-control form-control-lg"
        />
      ),
      edit: (<i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>),
      check: (
        <Checkbox
          onClick={() => onSelectOne(a.exa_cat_id)}
          checked={selectedHomeCatIds.indexOf(a.exa_cat_id) >= 0}
        />
      )
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableHomeCategory;
