import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getActiveExamsSuccess, getInactiveExamsSuccess } from './action';

import ExaminationCalendarRepository from '../../repositories/ExaminationCalendarRepository';

function* getActiveQuestionsSaga({ payload }) {
    try {
        const data = yield call(ExaminationCalendarRepository.getActiveExams, payload);
        yield put(getActiveExamsSuccess(data));
    } catch (err) {
        yield put(getActiveExamsSuccess(null));
    }
}

function* getInactiveQuestionsSaga({ payload }) {
    try {
        const data = yield call(ExaminationCalendarRepository.getInactiveExams, payload);
        yield put(getInactiveExamsSuccess(data));
    } catch (err) {
        yield put(getInactiveExamsSuccess(null));
    }
}


export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ACTIVE_EXAM_REQUEST, getActiveQuestionsSaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_EXAM_REQUEST, getInactiveQuestionsSaga)]);
}
