import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableCity from '../../../components/tables/changepassword';
import { getAllCity, getInactiveCity } from '../../../store/City/action';
import LocationControllerRepository from '../../../repositories/LocationControllerRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    //const { allCity, totalActiveCount, inactiveCity, totalInactiveCount } = useSelector(({ City }) => City);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);
    
    // useEffect(() => {
    //     setLoader(true);
    //     let ctr = {};
    //     ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
    //     ctr._limit = pageSizeTotal;
    //     dispatch(getAllCity(ctr));
    //     dispatch(getInactiveCity(ctr));
    // }, []);

    // useEffect(() => {
    //     setLoader(false);
    // }, [allCity, inactiveCity]);

    



    return (
        <>
          <div style={{ marginBottom: '0rem', padding: '10px 30px', borderRadius: '0rem', backgroundColor: '#eee' }} className="page-heading d-sm-flex justify-content-sm-between align-items-sm-center">
            <h2 className="title mb-3 mb-sm-0">Change Password</h2>
          </div>
          <div style={{ padding: '5%' }} className="col-12">
            {loader &&
              <div className="loader-view w-100"
                style={{ height: 'calc(100vh - 120px)' }}>
                <HeaderDashboard />
              </div>
            }
            {!loader &&
              <div>
                <div className="row no-gutters">
                  <div className="col-lg-12 d-flex flex-column order-lg-1">
                    <Tabs
                      required
                      disabled={true}
                      id="required"
                      label={'Username'}
                      name={'Username'}
                      defaultValue={''}//jai
                      margin="none" />
                    <Tabs
                      required
                      autoComplete={'off'}
                      id="required"
                      type={'password'}
                      label={'Old Password'}
                      name={'OldPassword'}
                      onChange={(event) => onOldPasswordChange(event.target.value)}
                      onBlur={''}//valiadateProperty
                      value={''}//jai
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['OldPassword']}</h6>
                    <Tabs
                      required
                      autoComplete={'off'}
                      id="required"
                      type={'password'}
                      label={'New Password'}
                      name={'NewPassword'}
                      onChange={(event) => onNewPasswordChange(event.target.value)}
                      onBlur={''}//valiadateProperty
                      value={''}//newPassword
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['NewPassword']}</h6>
                    <Tabs
                      required
                      type={'password'}
                      autoComplete={'off'}
                      id="required"
                      label={'Confirm Password'}
                      name={'ConfirmPassword'}
                      onChange={(event) => onConfirmPasswordChange(event.target.value)}
                      onBlur={''}//valiadateProperty
                      value={''}//confirmPassword
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['ConfirmPassword']}</h6>
                    <div style={{ paddingTop: '2%', textAlign: 'center' }} className="col-lg-12 col-sm-12 col-12">
                      <Button onClick={() => changePassword()} variant="contained" color="primary" className="jr-btn text-white">Submit</Button>
                    </div>
                  </div>
                </div>
                <Spin
                  className="mb-3 bg-info"
                  anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                  open={''}//showMessage
                  message={''}//alertMessage
                />
              </div>
            }
          </div>
        </>
      );
};


export default Home;
