import Repository, { apiUrl } from './Repository';

class AuthRepository {
    constructor(callback) {
        this.callback = callback;
    }

    async getAllAnnouncements(payload) {
        const reponse = await Repository.get(`${apiUrl}/Announcements/?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async getInactiveAnnouncements(payload) {
        const reponse = await Repository.get(`${apiUrl}/Announcements/inactive?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })

            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async saveAnnouncements(formdata) {
        const reponse = await Repository.post(`${apiUrl}/Announcements/`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async editAnnouncements(categoryId, formdata) {
        const reponse = await Repository.put(`${apiUrl}/Announcements/Id/${categoryId}`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async inactiveCategory(data) {
        const reponse = await Repository.put(`${apiUrl}/Announcements/inactive`, data)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async deleteCategory(data) {
        const reponse = await Repository.delete(`${apiUrl}/Announcements/`, {
            headers: {
                "Content-Type": "application/json"
            },
            data: data
        }).then(response => {
            return response.data;
        }).catch(error => ({ error: JSON.stringify(error) }));;
        return reponse;
    }

    async changePosition(data) {
        const reponse = await Repository.put(`${apiUrl}/Announcements/position`, data)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
 
}

export default new AuthRepository();
