import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableCity from '../../../components/tables/changepassword';
import { getAllCity, getInactiveCity } from '../../../store/City/action';
import LocationControllerRepository from '../../../repositories/LocationControllerRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const { allCity, totalActiveCount, inactiveCity, totalInactiveCount } = useSelector(({ City }) => City);
    const [showModal, setShowModal] = useState(false);
    const [cityname, setCityname] = useState('');
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [State, setState] = useState('');
    const [Citycode, setCitycode] = useState('');
    const [search, setsearch] = useState('');
    const [stateitems, setStateItems] = useState([]);
    const [activeState, setactiveState] = useState([]);

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);
    
    const handleRefresh = async () => {
        const stateresult = await LocationControllerRepository.getState();
        const { state: stateres } = stateresult;
        console.log(stateresult, "result")
        let itemArr = [];
        for (let state of stateres) {
            itemArr.push(<item value={state.state_id}>{state.state_name}</item>)
        }
        console.log(itemArr)
        setStateItems(itemArr);
        setactiveState(itemArr)
        setTimeout(() => {
            setLoader(false)
        }, 1000);
    }
    useEffect(() => {
        setLoader(true);
        async function fetchData() {
            handleRefresh()
        }
        fetchData();
    }, [])

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllCity(ctr));
        dispatch(getInactiveCity(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allCity, inactiveCity]);

    


    const closeModalOnClick = () => {
        setCityname('');
        setCitycode('');
        setSelectedHomeCatId('');
        setState('');
        setErrors({});
        setShowModal(false);
    }

    
    const save = async () => {
        if (cityname && Citycode) {
            setLoader(true);
            let obj = {
                "state_id": stateId,
                "city_name": cityname,
                "city_code": Citycode
            }
            try {
                await LocationControllerRepository.saveCity(obj);
                notification.success({
                    message: 'State Added Successfully.'
                })
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;

                if (search) {
                    ctr.search = search;
                }
                dispatch(getAllCity(ctr));
                dispatch(getInactiveCity(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!cityname) errorObj['cityname'] = "Please Enter cityname";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (cityname && selectedHomeCatId && Citycode && cityname) {
            setLoader(true);
            let obj = {
                "city_id": selectedHomeCatId,
                "city_name": cityname,
                "city_code": Citycode,
                "state_id": stateId,
            }
            try {
                await LocationControllerRepository.editCity(selectedHomeCatId, obj);
                notification.success({
                    message: 'State Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCity(ctr));
                dispatch(getInactiveCity(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!cityname) errorObj['cityname'] = "Please Enter cityname";
            setErrors(errorObj);
        }
    }

    return (
        <>
          <div style={{ marginBottom: '0rem', padding: '10px 30px', borderRadius: '0rem', backgroundColor: '#eee' }} className="page-heading d-sm-flex justify-content-sm-between align-items-sm-center">
            <h2 className="title mb-3 mb-sm-0">Change Password</h2>
          </div>
          <div style={{ padding: '5%' }} className="col-12">
            {loader &&
              <div className="loader-view w-100"
                style={{ height: 'calc(100vh - 120px)' }}>
                <CircularProgress />
              </div>
            }
            {!loader &&
              <div>
                <div className="row no-gutters">
                  <div className="col-lg-12 d-flex flex-column order-lg-1">
                    <TextField
                      required
                      disabled={true}
                      id="required"
                      label={'Username'}
                      name={'Username'}
                      defaultValue={userName}
                      margin="none" />
                    <TextField
                      required
                      autoComplete={'off'}
                      id="required"
                      type={'password'}
                      label={'Old Password'}
                      name={'OldPassword'}
                      onChange={(event) => onOldPasswordChange(event.target.value)}
                      onBlur={valiadateProperty}
                      value={oldPassword}
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['OldPassword']}</h6>
                    <TextField
                      required
                      autoComplete={'off'}
                      id="required"
                      type={'password'}
                      label={'New Password'}
                      name={'NewPassword'}
                      onChange={(event) => onNewPasswordChange(event.target.value)}
                      onBlur={valiadateProperty}
                      value={newPassword}
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['NewPassword']}</h6>
                    <TextField
                      required
                      type={'password'}
                      autoComplete={'off'}
                      id="required"
                      label={'Confirm Password'}
                      name={'ConfirmPassword'}
                      onChange={(event) => onConfirmPasswordChange(event.target.value)}
                      onBlur={valiadateProperty}
                      value={confirmPassword}
                      margin="normal" />
                    <h6 style={{ color: 'red' }}>{errors['ConfirmPassword']}</h6>
                    <div style={{ paddingTop: '2%', textAlign: 'center' }} className="col-lg-12 col-sm-12 col-12">
                      <Button onClick={() => changePassword()} variant="contained" color="primary" className="jr-btn text-white">Submit</Button>
                    </div>
                  </div>
                </div>
                <Snackbar
                  className="mb-3 bg-info"
                  anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
                  open={showMessage}
                  message={alertMessage}
                />
              </div>
            }
          </div>
        </>
      );
};


export default Home;
