useEffect(() => {
    let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
    let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
    if (localAuth && !localAuth.isLoggedIn) {
        window.location.href = "/";
    }
}, [auth]);

<div>
<Spin spinning={loader} tip={'Loading...'}>
    <HeaderDashboard />
    <div className="dashboard-container">
        <div className="row no-gutters">
            <div className="col-md-2">
                <div id="sidebar">
                    <Sidebar page={'Change Password'} />
                </div>
            </div>
            <div className="col-md-10">
                <div className="content content-width" id="style-2">
                    <div style={{ marginTop: 20 }}>
                        <TableCity
                            allCity={tab === "active" ? allCity : inactiveCity}
                            editModalOnClick={editModalOnClick}
                            onSelectAll={onSelectAll}
                            onSelectOne={onSelectOne}
                            selectAll={selectAll}
                            selectedHomeCatIds={selectedHomeCatIds}
                        />
                    </div>
                    <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                        <Pagination
                            total={tab === "active" ? totalActiveCount : totalInactiveCount}
                            defaultCurrent={1}
                            current={currentPage}
                            defaultPageSize={10}
                            pageSize={pageSizeTotal}
                            pageSizeOptions={['10', '20', '30']}
                            onChange={pageSizeChange}
                            showSizeChanger={true}
                        />
                    </div>
                </div>
            </div>
        </div>
    </div>
    <Modal
        visible={showModal}
        okText={'Save'}
        onCancel={closeModalOnClick}
        title={selectedHomeCatId ? "Edit Announcements " : "Add New Announcements "}
        width={800}
        onOk={saveOnClick}
    >
        <Spin spinning={loader} tip={'Loading...'}>
            <div className='row'>
                <div className="col-md-12">
                    <div className="form-group">
                        <label>Country <span style={{ color: 'red' }}>*</span></label>
                        <input
                            className="form-control"
                            type="text"
                            value={Country}
                            placeholder=""
                            onChange={(e) => CountryOnChange(e.target.value)}
                        />
                        {errors['Country'] &&
                            <span style={{ color: 'red' }}>{errors['Country']}</span>
                        }
                    </div>
                    <div className="form-group">
                        <label>statename <span style={{ color: 'red' }}>*</span></label>
                        <input
                            className="form-control"
                            type="text"
                            value={statename}
                            placeholder=""
                            onChange={(e) => statenameOnChange(e.target.value)}
                        />
                        {errors['statename'] &&
                            <span style={{ color: 'red' }}>{errors['statename']}</span>
                        }
                    </div>
                    <div className="form-group">
                        <label>countrycode <span style={{ color: 'red' }}>*</span></label>
                        <input
                            className="form-control"
                            type="text"
                            value={countrycode}
                            placeholder=""
                            onChange={(e) => countrycodeOnChange(e.target.value)}
                        />
                        {errors['countrycode'] &&
                            <span style={{ color: 'red' }}>{errors['countrycode']}</span>
                        }
                    </div>
                </div>
            </div>

        </Spin>
    </Modal>
</Spin>
</div>
