import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import ExampackageRepository from '../../../repositories/ExampackageRepository';
import Joi from 'joi-browser';


const Home = (props) => {
  const { auth } = useSelector(({ auth }) => auth);
  const [showModal, setShowModal] = useState(false);
  const { allMenu, userMenu } = useSelector(({ admin }) => admin);
  const [errors, setErrors] = useState({});
  const [loader, setLoader] = useState(false);
  const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
  const [Username, setUsername] = useState('');
  const [OldPassword, setOldPassword] = useState('');
  const [NewPassword, setNewPassword] = useState('');
  const [ConfirmPassword, setConfirmPassword] = useState('');
  const [userType, setUserType] = useState("");
  const [userLoginType, setUserLoginType] = useState("");
  const [data, setdata] = useState([]);
  console.log(allMenu, "allMenu(n)")
  console.log(userMenu, 'userMenu(n)')
  const schema = {
    OldPassword: Joi.string().required(),
    NewPassword: Joi.string().min(5).required(),
    ConfirmPassword: Joi.string().required(),
  }
  const getallMenu = () => {
    let array = [];
    if (allMenu) {
      array = allMenu.map(h => h.menu_id)
      console.log(array, "sssssssss34")
    }
    console.log(array,"allMenu")
  }

  const getuserMenu = () => {
    let array = [];
    if (userMenu) {
      array = userMenu.map(h => h.menu_id)
      console.log(array, "sssssssss45")
    } 
    console.log(array,"userMenu")
  }
  console.log(data)
  useEffect(() => {
    let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
    let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
    if (localAuth && !localAuth.isLoggedIn) {
      window.location.href = "/";
    }
  }, [auth]);



  const closeModalOnClick = () => {
    setSelectedHomeCatId('');
    setErrors({});
    setShowModal(false);
  }

  const UsernameOnChange = (Username) => {
    let errorObj = { ...errors };
    errorObj['Username'] = '';
    setUsername(Username);
    setErrors(errorObj);
  }

  const OldPasswordOnChange = (OldPassword) => {
    let errorObj = { ...errors };
    errorObj['OldPassword'] = '';
    setOldPassword(OldPassword);
    setErrors(errorObj);
  }

  const NewPasswordOnChange = (NewPassword) => {
    let errorObj = { ...errors };
    errorObj['setNewPassword'] = '';
    setNewPassword(NewPassword);
    setErrors(errorObj);
  }

  const ConfirmPasswordOnChange = (ConfirmPassword) => {
    let errorObj = { ...errors };
    errorObj['ConfirmPassword'] = '';
    setConfirmPassword(ConfirmPassword);
    setErrors(errorObj);
  }


  const saveOnClick = () => {
    if (selectedHomeCatId) {
      edit();
    } else {
      changePassword();
    }
  }

  const save = async () => {
    if (Username) {
      let obj = {
        "package_name": Username,
      }
      try {
        await ExampackageRepository.saveExampackage(obj);
        notification.success({
          message: 'Exampackage Added Successfully.'
        })
      } catch (e) {
        notification.error({
          message: 'Exampackage Added Failed.'
        });
      }
    } else {
      let errorObj = { ...errors };
      if (!Username) errorObj['Username'] = "Please Enter Username";
      setErrors(errorObj);
    }

  }

  useEffect(() => {
    //setLoader(true);
    getuserMenu()
  }, []);
  useEffect(() => {
    //setLoader(true);
    getallMenu()
  }, []);

  const valiadateProperty = (e) => {
    let { name, value, className } = e.currentTarget;
    const obj = { [name]: value };
    const filedSchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, filedSchema);

    let message = error ? error.details[0].message : null;
    setErrors({ ...errors, [name]: message, "errordetails": null })
  }

  const editModalOnClick = async (data) => {
    console.log(data, "data")
    setLoader(true);

    setUsername('')
    setOldPassword('')
    setConfirmPassword('')
    setNewPassword('')
    setUserType('')
    setLoader(false);
    setShowModal(true);
  }


  const changePassword = async () => {
    if (NewPassword != ConfirmPassword) {
      setErrors({ ...errors, ['ConfirmPassword']: 'Confirm Password must match New Password', "errordetails": null })
    } else {
      if (OldPassword &&
        NewPassword &&
        ConfirmPassword &&
        errors['ConfirmPassword'] === null &&
        errors['NewPassword'] === null &&
        errors['OldPassword'] === null) {
        try {
          let obj = {};
          obj.oldpassword = OldPassword;
          obj.newpassword = NewPassword;
          obj.type = userType;
          obj.logintype = userLoginType;
          let res = await adminService.changePassword(obj);
          console.log(res);
          setAlertMessage('Password Changed Successfully');
          await handleRefresh();
          setShowMessage(true);
          setTimeout(() => {
            setShowMessage(false)
          }, 1500);
        } catch (err) {
          console.log(err.response.data.error.message);
          setAlertMessage(err.response.data.error.message);
        }
      } else {
        console.log(errors);
        if (errors['ConfirmPassword'] != null ||
          errors['NewPassword'] != null ||
          errors['OldPassword'] != null) {
          setAlertMessage('Please Check the errors');
          setShowMessage(true);
          setTimeout(() => {
            setShowMessage(false)
          }, 1500);
        } else {
          let errorObj = { ...errors };
          if (!Username) errorObj['Username'] = "Please Enter Username";
          setErrors(errorObj);
        }
      }
    }
  }



  return (
    <div>
      <Spin spinning={loader} tip={'Loading...'}>
        <HeaderDashboard />
        <div className="dashboard-container">
          <div className="row no-gutters">
            <div className="col-md-2">
              <div id="sidebar">
                <Sidebar page={'Change Password'} />
              </div>
            </div>
            <div className="col-md-10">
              <div className="content content-width" id="style-2">
                <h2> Change or reset your password</h2>
                <h3>You can change your password for security reasons or reset it if you forget it.<br></br>
                  Your newapollo password is used to access many School or colleges, <br></br>
                  like Gmail and YouTube.</h3>
                <div style={{ marginTop: 20 }}>
                  <label>ChangePassword</label>
                  <button onClick={() => editModalOnClick()} className='button-class'>
                    <i className="fa fa-plus" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Modal
          visible={showModal}
          okText={'Save'}
          onCancel={closeModalOnClick}
          title=" ChangePassword "
          width={800}
          onOk={saveOnClick}
        >
          <Spin spinning={loader} tip={'Loading...'}>
            <div className='row'>
              <div className="col-md-12">
                <div className="form-group">
                  <label>Username<span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type="text"
                    value={Username}
                    placeholder=""
                    onChange={(e) => UsernameOnChange(e.target.value)}
                  />
                  {errors['Username'] &&
                    <span style={{ color: 'red' }}>{errors['Username']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>OldPassword <span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type="text"
                    value={OldPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => OldPasswordOnChange(e.target.value)}
                  />
                  {errors['OldPassword'] &&
                    <span style={{ color: 'red' }}>{errors['OldPassword']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>NewPassword <span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type="text"
                    value={NewPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => NewPasswordOnChange(e.target.value)}
                  />
                  {errors['NewPassword'] &&
                    <span style={{ color: 'red' }}>{errors['NewPassword']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>ConfirmPassword<span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type="text"
                    value={ConfirmPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => ConfirmPasswordOnChange(e.target.value)}
                  />
                  {errors['ConfirmPassword'] &&
                    <span style={{ color: 'red' }}>{errors['ConfirmPassword']}</span>
                  }
                </div>
              </div>
            </div>

          </Spin>
        </Modal>
      </Spin>
    </div>
  );
};


export default Home;
