import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllStateSuccess, getInactiveStateSuccess } from './action';

import StateRepository from '../../repositories/StateRepository';

function* getAllStateSaga({ payload }) {
    try {
        const data = yield call(StateRepository.getAllState, payload);
        yield put(getAllStateSuccess(data));
    } catch (err) {
        yield put(getAllStateSuccess(null));
    }
}

function* getInactiveStateSaga({ payload }) {
    try {
        const data = yield call(StateRepository.getInactiveState, payload);
        yield put(getInactiveStateSuccess(data));
    } catch (err) {
        yield put(getInactiveStateSuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_STATE_REQUEST, getAllStateSaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_STATE_REQUEST, getInactiveStateSaga)]);
}
