import React from 'react';
import { Checkbox, Table } from 'antd';

const TableDashboardCategory = ({ allCoupon, editModalOnClick,deleteOneCoupon, onSelectAll, onSelectOne, selectAll, selectedHomeCatIds, mainPositionOnChange, CouponValueChange }) => {
  const columns = [
    {
      title: 'CouponName',
      dataIndex: 'coupon_name',
    },
    {
      title: 'CouponValue',
      dataIndex: 'coupon_value',
    },
    {
      title: 'No.ofUsage',
      dataIndex: 'no_of_usage',
    },
    {
      title: 'Type',
      dataIndex: 'coupon_value_type',
    },
    {
      title: 'FromDate',
      dataIndex: 'from_date',
    },
    {
      title: 'ExpiryDate',
      dataIndex: 'to_date',
    },
    {
      title: 'Edit',
      dataIndex: 'edit',
    },
    {
      title: 'Delete',
      dataIndex: 'delete',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check'
    }
  ];
  
  let data = allCoupon.map(a => {
    console.log(data,'1234')
    let CouponValue = CouponValueChange.filter(p => p.catId === a.coupon_id);
    let obj = {
      key: a.coupon_id,
      category: a.coupon_name,
      position: (
        <input
          type="text"
          onChange={event => mainPositionOnChange(a.coupon_id, event.target.value)}
          style={{ width: '50px', textAlign: 'center', padding: '0px' }}
          value={CouponValue && CouponValue.length > 0 ? CouponValue[0].CouponValue : a.coupon_value}
          className="form-control form-control-lg"
        />
      ),
      edit: (<i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>),
      delete : (<i className="fa fa-trash" onClick={() => deleteOneCoupon(a)} style={{cursor: 'pointer'}}></i>),

      check: (
        <Checkbox
          onClick={() => onSelectOne(a.coupon_id)}
          checked={selectedHomeCatIds.indexOf(a.coupon_id) >= 0}
        />
      )
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableDashboardCategory;
