import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableState from '../../../components/tables/State';
import { getAllState, getInactiveState } from '../../../store/State/action';
import LocationControllerRepository from '../../../repositories/LocationControllerRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const { allState, totalActiveCount, inactiveState, totalInactiveCount } = useSelector(({ State }) => State);
    const [showModal, setShowModal] = useState(false);
    const [statename, setStatename] = useState('');
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedHomeCatIds, setSelectedHomeCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [Country, setCountry] = useState('');
    const [search, setsearch] = useState('');
    const [posotionChangeCategorys, setPosotionChangeCategorys] = useState([]);

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllState(ctr));
        dispatch(getInactiveState(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allState, inactiveState]);

    const addModalOnClick = async () => {
        setLoader(true);
        setStatename('');
        setSelectedHomeCatId('');
        setLoader(false);
        setShowModal(true);
    }

    const deleteOneState = (data) => {
        Modal.confirm({
            title: 'Confirm',
            content: 'Do you want to delete this State.',
            okText: 'Yes',
            onOk: () => deleteOneCategory(data),
            cancelText: 'No',
        });
    }

    const deleteOneCategory = async (data) => {
        let obj = { "AnnouncementsId": [data.state_id] };
        await LocationControllerRepository.deleteCategory(obj);
        Modal.success({
            title: 'State Deleted Successfully.'
        });
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllState(ctr));
        dispatch(getInactiveState(ctr));
    }

    const editModalOnClick = async (data) => {
        setLoader(true);
        setSelectedHomeCatId(data.state_id);
        setCountry(data.Country);
        setLoader(false);
        setShowModal(true);
    }

    const closeModalOnClick = () => {
        setStatename('');
        setSelectedHomeCatId('');
        setCountry('');
        setErrors({});
        setShowModal(false);
    }

    const CountryOnChange = (Country) => {
        let errorObj = { ...errors };
        errorObj['Country'] = '';
        setCountry(Country);
        setErrors(errorObj);
    }

    const statenameOnChange = (statename) => {
            let errorObj = { ...errors };
            errorObj['statename'] = '';
            setStatename(statename);
            setErrors(errorObj);
    }
    const saveOnClick = () => {
        if (selectedHomeCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (statename && selectedHomeCatId) {
            setLoader(true);
            let obj = {
                "country_id": selectedHomeCatId,
                "state_name": statename,
                "country_code": countrycode
            }
            try {
                await LocationControllerRepository.saveState(obj);
                notification.success({
                    message: 'State Added Successfully.'
                })
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;

                if (search) {
                    ctr.search = search;
                }
                dispatch(getAllState(ctr));
                dispatch(getInactiveState(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!statename) errorObj['statename'] = "Please Enter statename";
            if (!countryId) errorObj['country'] = "Please Enter country";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (statename && countryId) {
            setLoader(true);
            let obj = {
                "country_id": selectedHomeCatId,
                "state_name": statename,
                "country_code": countrycode
            }
            try {
                await LocationControllerRepository.editState(selectedHomeCatId, obj);
                notification.success({
                    message: 'State Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllState(ctr));
                dispatch(getInactiveState(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!statename) errorObj['statename'] = "Please Enter statename";
            if (!countryId) errorObj['country'] = "Please Enter country";
            setErrors(errorObj);
        }
    }
  
    const searchOnChange = (search) => {
        console.log(search,"12345")
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.search = search;
        if (search) {
            ctr.search = search;
        }
        console.log(ctr,"12345")
        if (tab === 'active') {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setsearch(search);
        setCurrentPage(1);
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;

        if (tab === "active") {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
        setsearch('');
        setPosotionChangeCategorys([]);
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allState.map(h => h.state_id);
            } else {
                array = inactiveState.map(h => h.state_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedHomeCatIds];
        let array1 = tab === 'active' ? [...allState] : [...inactiveState];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedHomeCatIds];
        let obj = {
            AnnouncementsId: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await LocationControllerRepository.inactiveCategory(obj);
                notification.success({
                    message: 'State  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await LocationControllerRepository.inactiveCategory(obj);
                notification.success({
                    message: 'State  Updated Successfully.'
                });
            }
            if (action === "delete") {
                await LocationControllerRepository.deleteCategory(obj);
                notification.success({
                    message: 'State  Deleted Successfully.'
                });
            }
            setSelectedHomeCatIds([]);
            setPosotionChangeCategorys([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;

            if (search) {
                ctr.search = search;
            }
            dispatch(getAllState(ctr));
            dispatch(getInactiveState(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'State'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>

                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group ">
                                            <input
                                                className="form-control"
                                                type="text"
                                                value={search}
                                                placeholder="search"
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 20 }}>
                                    <TableState
                                        allState={tab === "active" ? allState : inactiveState}
                                        editModalOnClick={editModalOnClick}
                                        deleteOneState={deleteOneState}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedHomeCatIds={selectedHomeCatIds}
                                        mainPositionOnChange={mainPositionOnChange}
                                        posotionChangeCategorys={posotionChangeCategorys}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedHomeCatId ? "Edit Announcements " : "Add New Announcements "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Country <span style={{ color: 'red' }}>*</span></label>
                                    <textarea style={{ height: '100px' }}
                                        className="form-control"
                                        type="text"
                                        value={Country}
                                        placeholder=""
                                        onChange={(e) => CountryOnChange(e.target.value)}
                                    > </textarea>
                                    {errors['Country'] &&
                                        <span style={{ color: 'red' }}>{errors['Country']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>statename <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={statename}
                                        placeholder=""
                                        onChange={(e) => statenameOnChange(e.target.value)}
                                    />
                                    {errors['statename'] &&
                                        <span style={{ color: 'red' }}>{errors['statename']}</span>
                                    }
                                </div>
                            </div>
                        </div>

                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
