import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Spin, notification, Pagination, Tabs, Select } from 'antd';
import Link from 'next/link';
import Button from '@material-ui/core/Button';

import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableExams from '../../../components/tables/TableExams';

import { getQuery } from '../../../helper/auth';
import { getActiveExams, getInactiveExams } from '../../../store/exams/action';
import ExaminationCalendarRepository from '../../../repositories/ExaminationCalendarRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();

    const { auth } = useSelector(({ auth }) => auth);
    const {
        allExaminationCalendary,
        totalActiveCount,
        inactiveExaminationCalendar,
        totalInactiveCount,
    } = useSelector(({ Exams }) => Exams);
    const [errors, setErrors] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [mainCatId, setMainCatId] = useState('');
    const [mainCatName, setMainCatName] = useState('');
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCatId, setSelectedCatId] = useState('');
    const [selectedCatIds, setSelectedCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [examinationDate, setExaminationDate] = useState('');
    const [notificationDate, setNotificationDate] = useState('');
    const [search, setSearch] = useState('');



    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        let query = getQuery();
        setLoader(true);
        let ctr = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            mainExamId: query.exam_id,
            status: "Y"
        };
        let ctr1 = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            mainExamId: query.exam_id,
            status: "N"
        };
       
        setMainCatId(query.exam_id);
        setMainCatName(query.exam_name);
        dispatch(getActiveExams(ctr));
        dispatch(getInactiveExams(ctr1));
        let tab = query.status === 'Y' ? 'active'  : 'inactive';
        setTab(tab);
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allExaminationCalendary]);

    const nameOnChange = (name) => {
        let errorObj = { ...errors };
        let slug = (name).replace(/ /g, "-").toLowerCase();
        errorObj['name'] = '';
        errorObj['slug'] = '';
        setName(name);
        setSlug(slug);
        setErrors(errorObj);
    }

    const slugOnChange = (slug) => {
        let errorObj = { ...errors };
        slug = (slug).replace(/ /g, "-").toLowerCase();
        errorObj['slug'] = '';
        setSlug(slug);
        setErrors(errorObj);
    }
    const notificationDateOnChange = (notificationDate) => {
        let errorObj = { ...errors };
        errorObj['notificationDate'] = '';
        setNotificationDate(notificationDate);
        setErrors(errorObj);
    }
    const examinationDateOnChange = (examinationDate) => {
        let errorObj = { ...errors };
        errorObj['examinationDate'] = '';
        setExaminationDate(examinationDate);
        setErrors(errorObj);
    }
    const positionOnChange = (position) => {
        const re = /^[0-9\b]+$/; //rules
        if (position === "" || re.test(position)) {
            let errorObj = { ...errors };
            errorObj['position'] = '';
            setPosition(position);
            setErrors(errorObj);
        }
    }

    const editModalOnClick = async (data) => {
        setLoader(true);
      setMainCatName(data.exam_name);
        setMainCatId(data.exam_id);
        setLoader(false);
       setShowModal(true);
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {
            _start: page === 1 ? 0 : ((page - 1) * pageSize),
            _limit: pageSize,
            exam_id: mainCatId,
        };
        if (search) {
            ctr.search = search;
        }
       
       
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedCatIds([]);
        setSelectAll(false);
        setAction('');
        setSearch('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allExaminationCalendary.map(h => h.exam_id);
            } else {
                array = inactiveExaminationCalendar.map(h => h.exam_id);
            }
        }
        setSelectedCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCatIds];
        let array1 = tab === 'active' ? [...allExaminationCalendary] : tab === 'inactive' ? [...waitingQuestions] : [...inactiveExaminationCalendar];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedCatIds(array);
    }
    const closeModalOnClick = () => {
        setMainCatName('')
        setErrors({});
        setShowModal(false);
    }
    const addModalOnClick = async () => {
        setLoader(true);
        setMainCatName('')
        setLoader(false);
        setShowModal(true);
    }
    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedCatIds];
        let obj = {
            qid: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await ExaminationCalendarRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await ExaminationCalendarRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "delete") {
                await ExaminationCalendarRepository.deleteCategory(obj);
                notification.success({
                    message: 'Quetions Deleted Successfully.'
                });
            }
            setSelectedCatIds([]);
            let ctr = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                exam_id: mainCatId,
                status: "Y"
            };
            let ctr1 = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                exam_id: mainCatId,
                status: "N"
            };
            if (search) {
                ctr.search = search;
                ctr1.search = search;
            }
          
          
         
            dispatch(getActiveExams(ctr));
            dispatch(getInactiveExams(ctr1));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }
    const saveOnClick = () => {
        if (selectedCatId) {
            edit();
        } else {
            save();
        }
    }
    const searchOnChange = (search) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        ctr['search'] = search;
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setSearch(search);
        setCurrentPage(1);
    }

    const resetOnClick = () => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setSearch('');
        setCurrentPage(1);
    }

    return (
        <div>
             
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Examination Calendar'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs activeKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <Button
                                            variant="contained"
                                            onClick={goOnClick}
                                            style={{
                                                backgroundColor: "#cd4040",
                                                color: '#fff',
                                                height: 30
                                            }}
                                            className="jr-btn"
                                        >
                                            Go
                                        </Button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>
                                   
                                </div>
                                <div className='row'>
                                <div className="col-md-4">
                                        <div className="form-group">
                                            <input
                                                className="form-control"
                                                type="text"
                                                placeholder="Search"
                                                style={{ height: 33 }}
                                                value={search}
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-8">
                                        <div className="form-group">
                                            <Button
                                                variant="contained"
                                                onClick={resetOnClick}
                                                style={{
                                                    backgroundColor: "#2196f3",
                                                    color: '#fff',
                                                    height: 30
                                                }}
                                                className="jr-btn"
                                            >
                                                <i className="fas fa-align-center" /> Reset
                                            </Button>
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 10 }}>
                                    <TableExams
                                        calendarData={tab === "active" ? allExaminationCalendary : inactiveExaminationCalendar}
                                        editModalOnClick={editModalOnClick}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCatIds={selectedCatIds}
                                        mainCatId={mainCatId}
                                        mainCatName={mainCatName}
                                    />
                                </div>
                                <div style={{ margin: '10px auto', textAlign: 'right' }}>
                                    <Pagination
                                        total={tab === "active" ? allExaminationCalendary : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedCatId ? "Edit Exam" : "Add New Exam"}
                    width={800}
                    onOk={saveOnClick}>
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Exam Name <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={mainCatName}
                                        placeholder=""
                                        onChange={(e) => nameOnChange(e.target.value)}
                                    />
                                    {errors['mainCatName'] &&
                                        <span style={{ color: 'red' }}>{errors['mainCatName']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Exam slug <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={mainCatName}
                                        placeholder=""
                                        onChange={(e) => slugOnChange(e.target.value)}
                                    />
                                    {errors['mainCatName'] &&
                                        <span style={{ color: 'red' }}>{errors['mainCatName']}</span>
                                    }
                                </div>
                                
                                <div className="form-group">
                                    <label>Notification Date <span style={{ color: 'red' }}>*</span></label>
                                    <textarea style={{ height: '100px' }}
                                        className="form-control"
                                        type="text"
                                        value={notificationDate}
                                        placeholder=""
                                        onChange={(e) => notificationDateOnChange(e.target.value)}
                                    > </textarea>
                                    {errors['notificationDate'] &&
                                        <span style={{ color: 'red' }}>{errors['notificationDate']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>ExaminationDate <span style={{ color: 'red' }}>*</span></label>
                                    <textarea style={{ height: '100px' }}
                                        className="form-control"
                                        type="text"
                                        value={examinationDate}
                                        placeholder=""
                                        onChange={(e) => examinationDateOnChange(e.target.value)}
                                    > </textarea>
                                    {errors['examinationDate'] &&
                                        <span style={{ color: 'red' }}>{errors['examinationDate']}</span>
                                    }
                                </div>
                                    
                                <div className="form-group">
                                    <label>Position <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={position}
                                        placeholder=""
                                        onChange={(e) => positionOnChange(e.target.value)}
                                    />
                                    {errors['position'] &&
                                        <span style={{ color: 'red' }}>{errors['position']}</span>
                                    }
                                </div>
                            </div>
                        </div>
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
