import Repository, { apiUrl } from './Repository';

class AuthRepository {
    constructor(callback) {
        this.callback = callback;
    }

    async getAllState(payload) {
        const reponse = await Repository.get(`${apiUrl}/location/state/Y?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async getAllCountry(payload) {
        const reponse = await Repository.get(`${apiUrl}/location/country/Y?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async getInactiveCountry(payload) {
        const reponse = await Repository.get(`${apiUrl}/location/country/N?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async getInactiveState(payload) {
        const reponse = await Repository.get(`${apiUrl}/location/state/N?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
   
    async saveState(formdata) {
        const reponse = await Repository.post(`${apiUrl}/location/state/`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async editState(categoryId, formdata) {
        const reponse = await Repository.put(`${apiUrl}/location/State/${categoryId}`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async inactiveCategory(data) {
        const reponse = await Repository.put(`${apiUrl}/State/inactive`, data)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

    async deleteCategory(data) {
        const reponse = await Repository.delete(`${apiUrl}/State/`, {
            headers: {
                "Content-Type": "application/json"
            },
            data: data
        }).then(response => {
            return response.data;
        }).catch(error => ({ error: JSON.stringify(error) }));;
        return reponse;
    }

  
}

export default new AuthRepository();
