                                        placeholder=""
                                        onChange={(e) => studemailOnChange(e.target.value)}

                                        
                                    />
                                    {errors['studemail'] &&
                                        <span style={{ color: 'red' }}>{errors['studemail']}</span>
                                    }
                                </div>
                             
                                <div className="form-group">
