import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableCoupon from '../../../components/tables/Coupon';

import { getAllCoupon, getInactiveCoupon } from '../../../store/Coupon/action';
import DashboardCategoryRepository from '../../../repositories/CouponRepository';
import Moment from "moment";

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const valueRef = React.createRef()
    const { auth } = useSelector(({ auth }) => auth);
    const { allCoupon, totalActiveCount, inactiveDashboardCategory, totalInactiveCount } = useSelector(({ Coupon }) => Coupon);

    const [showModal, setShowModal] = useState(false);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCouponId, setSelectedHomeCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [fromDate, setFromDate] = useState(Moment(new Date()).format('MM-DD-YYYY'));
    const [toDate, setToDate] = useState(Moment(new Date()).format('MM-DD-YYYY'));
    const [data, setData] = useState([])
    const [modal, setModal] = useState(false)
    const [couponId, setCouponId] = useState('');
    const [couponName, setCouponName] = useState('');
    const [couponCode, setCouponCode] = useState('');
    const [couponValue, setCouponValue] = useState(0);
    const [couponValueType, setCouponValueType] = useState('');
    const [cartValueRange, setCartValueRange] = useState(0);
   const [noOfUsage, setNoOfUsage] = useState('');
   const [noOfUsageUser, setNoOfUsageUser] = useState('');
    const [cartValueRangeType, setCartValueRangeType] = useState('Less Than');
    const [cartValue, setCartValue] = useState(0);
    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllCoupon('Y'));
        dispatch(getInactiveCoupon('N'));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allCoupon, inactiveDashboardCategory]);

    const addModalOnClick = async () => {
        setLoader(true);
    //    let result = await HomeCategoryRepository.getAllHomeCategory({ _start: 0, _limit: 10000 });
        // if (result && result.category && result.category.length > 0) {
        //     setMasterCategory(result.category);
        // }
        setCouponName('');
        setLoader(false);
        setShowModal(true);
    }
    // const getAllInactive = async () => {
    //     setLoader(true);
    //     setDataType("Inactive");
    //     setTxtClass('bg-danger text-white');
    //     setAction('');
    //     const { data: inactiveres } = await couponService.getAllCoupon('N');
    //     const { coupon: inactivedata } = inactiveres;
    //     setData(inactivedata);
    //     setTimeout(() => {
    //       setLoader(false)
    //     }, 1000);
    //   }
    
    //   const getAllActive = async () => {
    //     setLoader(true);
    //     setDataType("Active");
    //     setTxtClass('bg-success text-white');
    //     setAction('');
    //     const { data: activeres } = await couponService.getAllCoupon('Y');
    //     const { coupon: activedata } = activeres;
    //     setData(activedata);
    //     setTimeout(() => {
    //       setLoader(false)
    //     }, 1000);
    //   }
    
    const deleteOneDashboardCategory = (data) => {
        Modal.confirm({
            title: 'Confirm',
            content: 'Do you want to delete this DashboardCategory.',
            okText: 'Yes',
            onOk: () => deleteOneCategory(data),
            cancelText: 'No',
        });
    }

    const deleteOneCategory = async (data) => {
        let obj = { "catId": [data.cat_id] };
        await DashboardCategoryRepository.deleteCategory(obj);
        Modal.success({
            title: 'Dashboard Category Deleted Successfully.'
        });
        let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCoupon(ctr));
                dispatch(getInactiveCoupon(ctr));
    }

    const editModalOnClick = async (data) => {
        console.log(data, 'aaa')
        setLoader(true);
        setCouponName(data.coupon_name);
        setCouponId(data.coupon_id);
        setLoader(false);
        setShowModal(true);
    }

    const closeModalOnClick = () => {
        setCouponName('');
        setCouponId('');
        setErrors({});
        setShowModal(false);
    }

    const saveOnClick = () => {
        if (couponId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (couponName    ) {
            setLoader(true);
            let obj = {
                "cat_name": couponName,
            }
            try {
                await DashboardCategoryRepository.savedashboardCategory(obj);
                notification.success({
                    message: 'Dashboard Category  Added Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCoupon('Y'));
              dispatch(getInactiveCoupon('N'));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Dashboard Category  Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!couponName) errorObj['couponName'] = "Please Enter Title";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (couponName &&  couponId  ) {
            setLoader(true);
            let obj = {
                "cat_name": couponName,
                "cat_id": couponId,
            }
            try {
                await DashboardCategoryRepository.editdashboardCategory(couponId, obj);
                notification.success({
                    message: 'Dashboard Category  Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllCoupon('Y'));
            dispatch(getInactiveCoupon('N'));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Dashboard Category  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!couponName) errorObj['couponName'] = "Please Enter Title";
            setErrors(errorObj);
        }
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;
        if (tab === "active") {
            dispatch(getAllCoupon(ctr));
           ;
        } else {
            dispatch(getInactiveCoupon(ctr))
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllCoupon(ctr));
        } else {
            dispatch(getInactiveCoupon(ctr))
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
      //  setPosotionChangeCategorys([]);
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allCoupon.map(h => h.cat_id);
            } else {
                array = inactiveDashboardCategory.map(h => h.cat_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCouponId];
        let array1 = tab === 'active' ? [...allCoupon] : [...inactiveDashboardCategory];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }


    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedCouponIdArr = [...selectedCouponId];
        let obj = {
            catId: selectedCouponIdArr
        };
        if (selectedCouponIdArr && selectedCouponIdArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await DashboardCategoryRepository.changeStatus(obj);
                notification.success({
                    message: 'Dashboard Category  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await DashboardCategoryRepository.changeStatus(obj);
                notification.success({
                    message: 'Dashboard Category  Updated Successfully.'
                });
            }
            if (action === "delete") {
                await DashboardCategoryRepository.deleteCategory(obj);
                notification.success({
                    message: 'Dashboard Category  Deleted Successfully.'
                });
            }
            
            setSelectedHomeCatIds([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;
            dispatch(getAllCoupon(ctr));
            dispatch(getInactiveCoupon(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedCouponIdArr
                .length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Dashboard Category'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>
                                </div>
                                <div style={{ marginTop: 20 }}>
                                    <TableCoupon
                                        allCoupon={tab === "active" ? allCoupon : inactiveDashboardCategory}
                                        editModalOnClick={editModalOnClick}
                                        deleteOneDashboardCategory={deleteOneDashboardCategory}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCouponId={selectedCouponId}
                                      //  mainPositionOnChange={mainPositionOnChange}
                                      //  posotionChangeCategorys={posotionChangeCategorys}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={couponId ? "Edit Coupon " : "Add New Coupon "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label>Coupon Name <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponName}
                                        placeholder=""
                                    />
                                    {errors['couponName'] &&
                                        <span style={{ color: 'red' }}>{errors['couponName']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>coupon Value <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponValue}
                                        placeholder=""
                                    />
                                    {errors['couponValue'] &&
                                        <span style={{ color: 'red' }}>{errors['couponValue']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Cart Value Range Type <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={cartValueRangeType}
                                        placeholder=""
                                    />
                                    {errors['cartValueRangeType'] &&
                                        <span style={{ color: 'red' }}>{errors['cartValueRangeType']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>No.of Usage <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={noOfUsage}
                                        placeholder=""
                                    />
                                    {errors['noOfUsage'] &&
                                        <span style={{ color: 'red' }}>{errors['noOfUsage']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>from Date <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={fromDate}
                                        placeholder=""
                                    />
                                    {errors['fromDate'] &&
                                        <span style={{ color: 'red' }}>{errors['fromDate']}</span>
                                    }
                                </div>
                              
                            </div>
                            <div className="col-md-6">
                                <div className="form-group">
                                    <label>CouponCode <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponCode}
                                        placeholder=""
                                    />
                                    {errors['couponCode'] &&
                                        <span style={{ color: 'red' }}>{errors['couponCode']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Type <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={couponValueType}
                                        placeholder=""
                                    />
                                    {errors['couponValueType'] &&
                                        <span style={{ color: 'red' }}>{errors['couponValueType']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>CartValueRange <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={cartValueRange}
                                        placeholder=""
                                    />
                                    {errors['cartValueRange'] &&
                                        <span style={{ color: 'red' }}>{errors['cartValueRange']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>No.of Usage Per User <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={noOfUsageUser}
                                        placeholder=""
                                    />
                                    {errors['noOfUsageUser'] &&
                                        <span style={{ color: 'red' }}>{errors['noOfUsageUser']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Expiry Date <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={toDate}
                                        placeholder=""
                                    />
                                    {errors['toDate'] &&
                                        <span style={{ color: 'red' }}>{errors['toDate']}</span>
                                    }
                                </div>
                                
                            </div>
                        </div>
                        <div>
                        </div>
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
