import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import { getAllExampackage, getInactiveExampackage } from '../../../store/Exampackage/action';
import ExampackageRepository from '../../../repositories/ExampackageRepository';


const Home = (props) => {
    const { TabPane } = Tabs;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const [showModal, setShowModal] = useState(false);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [Username, setUsername] = useState('');
    const [chapterId, setChapterId] = useState('');
    const [OldPassword, setOldPassword] = useState([]);
    const [subcategory, setsubcategory] = useState([]);
   


   
    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    
    
 




    const editModalOnClick = async (data) => {
        console.log(data, "data")
        setLoader(true);
      
        setUsername('')
        setChapterId('')
        setOldPassword('')
        setsubcategory('')
        setLoader(false);
        setShowModal(true);
    }

    const closeModalOnClick = () => {
        setSelectedHomeCatId('');
        setErrors({});
        setShowModal(false);
    }

    const UsernameOnChange = (Username) => {
        let errorObj = { ...errors };
        errorObj['Username'] = '';
        setUsername(Username);
        setErrors(errorObj);
    }

    const MasterCategoryOnChange = (OldPassword) => {
        let errorObj = { ...errors };
        errorObj['OldPassword'] = '';
        setOldPassword(OldPassword);
        setErrors(errorObj);
    }

    

    const SubCategoryOnChange = (subcategory) => {
        let errorObj = { ...errors };
        errorObj['subcategory'] = '';
        setsubcategory(subcategory);
        setErrors(errorObj);
    }

   
    const saveOnClick = () => {
        if (selectedHomeCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (Username ) {
                let obj = {
                    "package_name": Username,
                    "chapt_id": chapterId,
                }
                try {
                    await ExampackageRepository.saveExampackage(obj);
                    notification.success({
                        message: 'Exampackage Added Successfully.'
                    })
                 
                    dispatch(getAllExampackage(ctr));
                    dispatch(getInactiveExampackage(ctr));
                    closeModalOnClick();
                } catch (e) {
                    notification.error({
                        message: 'Exampackage Added Failed.'
                    });
                }
            } else {
                let errorObj = { ...errors };
                if (!Username) errorObj['Username'] = "Please Enter Username";
                setErrors(errorObj);
            }
        
    }

    const edit = async () => {
        if (selectedHomeCatId) {
            setLoader(true);
            let obj = {
                "package_id": selectedHomeCatId,
                "package_name": Username,
                "chapt_id": chapterId,
            }
            try {
                await ExampackageRepository.editExampackage(selectedHomeCatId, obj);
                notification.success({
                    message: 'Exampackage Updated Successfully.'
                });
               
           
            } catch (e) {
                notification.error({
                    message: 'Exampackage  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!Username) errorObj['Username'] = "Please Enter Username";
            setErrors(errorObj);
        }
    }
  

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Change Password'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <div style={{ marginTop: 20 }}>
                                  <label>ChangePassword</label>
                                <button onClick={() => editModalOnClick()} className= 'button-class'>
                            <i className="fa fa-plus" />
                        </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedHomeCatId ? "Edit Exampackage " : "Add New Exampackage "}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Username<span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={Username}
                                        placeholder=""
                                        onChange={(e) => UsernameOnChange(e.target.value)}
                                    />
                                    {errors['Username'] &&
                                        <span style={{ color: 'red' }}>{errors['Username']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Master Category <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={OldPassword}
                                        placeholder=""
                                        onChange={(e) => MasterCategoryOnChange(e.target.value)}
                                    />
                                    {errors['OldPassword'] &&
                                        <span style={{ color: 'red' }}>{errors['OldPassword']}</span>
                                    }
                                </div>
                               
                                <div className="form-group">
                                    <label>  Sub Category<span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={subcategory}
                                        placeholder=""
                                        onChange={(e) => SubCategoryOnChange(e.target.value)}
                                    />
                                    {errors['subcategory'] &&
                                        <span style={{ color: 'red' }}>{errors['subcategory']}</span>
                                    }
                                </div>
                            </div>
                        </div>
                      
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
