import Repository, { apiUrl } from './Repository';

class AuthRepository {
    constructor(callback) {
        this.callback = callback;
    }
    async getAllCoupon(payload) {
        console.log("1234", payload)
        const reponse = await Repository.get(`${apiUrl}/coupon/status/Y?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async getInactiveCoupon(payload) {
        const reponse = await Repository.get(`${apiUrl}/coupon/status/N?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })

            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async saveCoupon(formdata) {
        console.log("12345689")
        const reponse = await Repository.post(`${apiUrl}/coupon/`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async updateCoupon(categoryId, formdata) {
        console.log("12345689")
        const reponse = await Repository.put(`${apiUrl}/coupon/id/${categoryId}`, formdata)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async changeStatus(data) {
        const reponse = await Repository.put(`${apiUrl}/coupon/status`, data)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }
    async deleteCoupon(data) {
        const reponse = await Repository.delete(`${apiUrl}/coupon/`, {
            headers: {
                "Content-Type": "application/json"
            },
            data: data
        }).then(response => {
            return response.data;
        }).catch(error => ({ error: JSON.stringify(error) }));;
        return reponse;
    }
    async getAlldurations(payload) {
        console.log("1234", payload)
        const reponse = await Repository.get(`${apiUrl}/coupon/duration/?_start=${payload._start}&_limit=${payload._limit}`)
            .then(response => {
                return response.data;
            })
            .catch(error => ({ error: JSON.stringify(error) }));
        return reponse;
    }

}

export default new AuthRepository();
