import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllHomeCategorySuccess, getInactiveHomeCategorySuccess } from './action';

import HomeCategoryRepository from '../../repositories/HomeCategoryRepository';

function* getAllHomeCategorySaga({ payload }) {
    try {
        const data = yield call(HomeCategoryRepository.getAllHomeCategory, payload);
        yield put(getAllHomeCategorySuccess(data));
    } catch (err) {
        yield put(getAllHomeCategorySuccess(null));
    }
}

function* getInactiveHomeCategorySaga({ payload }) {
    try {
        const data = yield call(HomeCategoryRepository.getInactiveHomeCategory, payload);
        yield put(getInactiveHomeCategorySuccess(data));
    } catch (err) {
        yield put(getInactiveHomeCategorySuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_HOME_CATEGORY_REQUEST, getAllHomeCategorySaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_HOME_CATEGORY_REQUEST, getInactiveHomeCategorySaga)]);
}
