import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import { login} from '../../../store/auth/action';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import adminService from '../../../repositories/AdminRepository';
import Joi from 'joi-browser';


const Home = (props) => {
  const { auth } = useSelector(({ auth }) => auth);
  const [showModal, setShowModal] = useState(false);
  const [errors, setErrors] = useState({});
  const [loader, setLoader] = useState(false);
  const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
  const [Username, setUsername] = useState('');
  const [OldPassword, setOldPassword] = useState('');
  const [NewPassword, setNewPassword] = useState('');
  const [ConfirmPassword, setConfirmPassword] = useState('');
  const [userType, setUserType] = useState("");
  const [userLoginType, setUserLoginType] = useState("");
  const schema = {
    OldPassword: Joi.string().required(),
    NewPassword: Joi.string().min(5).required(),
    ConfirmPassword: Joi.string().required(),
  }
 
  useEffect(() => {
    let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
    let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
    if (localAuth && !localAuth.isLoggedIn) {
      window.location.href = "/";
    }
  }, [auth]);



  const closeModalOnClick = () => {
    setSelectedHomeCatId('');
    setErrors({});
    setShowModal(false);
  }

  const UsernameOnChange = (Username) => {
    let errorObj = { ...errors };
    errorObj['Username'] = '';
    setUsername(Username);
    setErrors(errorObj);
  }

  const OldPasswordOnChange = (OldPassword) => {
    let errorObj = { ...errors };
    errorObj['OldPassword'] = '';
    setOldPassword(OldPassword);
    setErrors(errorObj);
  }

  const NewPasswordOnChange = (NewPassword) => {
    let errorObj = { ...errors };
    errorObj['setNewPassword'] = '';
    setNewPassword(NewPassword);
    setErrors(errorObj);
  }

  const ConfirmPasswordOnChange = (ConfirmPassword) => {
    let errorObj = { ...errors };
    errorObj['ConfirmPassword'] = '';
    setConfirmPassword(ConfirmPassword);
    setErrors(errorObj);
  }


  const saveOnClick = () => {
    if (selectedHomeCatId) {
      changePassword();
    }else{
      handleRefresh()
    }
  }

  const handleRefresh = async () => {
    setLoader(true);
    setOldPassword('');
    setNewPassword('');
    setConfirmPassword('');
   // let user = auth.login();
    user =dispatch(login(ctr));
    console.log(user);
    setUsername(user.user.name);
    setUserType(user.user.type);
    setUserLoginType(user.user.logintype);
    setTimeout(() => {
      setLoader(false)
    }, 1000);
  }

  
  useEffect(() => {
       handleRefresh()
  }, [])

  const valiadateProperty = (e) => {
    let { name, value, className } = e.currentTarget;
    const obj = { [name]: value };
    const filedSchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, filedSchema);

    let message = error ? error.details[0].message : null;
    setErrors({ ...errors, [name]: message, "errordetails": null })
  }

  const editModalOnClick = async (data) => {
    console.log(data, "data")
    setLoader(true);

    setUsername('')
    setOldPassword('')
    setConfirmPassword('')
    setNewPassword('')
    setUserType('')
    setLoader(false);
    setShowModal(true);
  }


  const changePassword = async (obj) => {
    console.log(obj,"qq");
    if (NewPassword != ConfirmPassword) {
      setErrors({ ...errors, ['ConfirmPassword']: 'Confirm Password must match New Password', "errordetails": null })
    } else {
      console.log(obj,"qq1");
      if (OldPassword &&
        NewPassword &&
        ConfirmPassword &&
        errors['ConfirmPassword'] === null &&
        errors['NewPassword'] === null &&
        errors['OldPassword'] === null) {
          console.log(obj,"qq2");
          let obj = {
            OldPassword : OldPassword,
            NewPassword : NewPassword,
            userType : userType,
            userLoginType : userLoginType,
        };
        try {
          console.log(obj,"qq3");
          let res = await adminService.changePassword(obj);
          console.log(res,"qq");
          setAlertMessage('Password Changed Successfully');

        } catch (err) {
          notification.error({
            message: 'Password Changed Failed.'
        });
        }
      } else {
        let errorObj = { ...errors };
        if (!OldPassword) errorObj['OldPassword'] = "Please Enter OldPassword";
        if (!NewPassword) errorObj['NewPassword'] = "Please Enter NewPassword";
        if (!ConfirmPassword) errorObj['ConfirmPassword'] = "Please Enter ConfirmPassword";
        //   setAlertMessage('Please Check the errors');
        setErrors(errorObj);
      
      }
    }
  }
  return (
    <div>
      <Spin spinning={loader} tip={'Loading...'}>
        <HeaderDashboard />
        <div className="dashboard-container">
          <div className="row no-gutters">
            <div className="col-md-2">
              <div id="sidebar">
                <Sidebar page={'Change Password'} />
              </div>
            </div>
            <div className="col-md-10">
              <div className="content content-width" id="style-2">
                <h2> Change or reset your password</h2>
                <h3>You can change your password for security reasons or reset it if you forget it.<br></br>
                  Your newapollo password is used to access many School or colleges, <br></br>
                  like Gmail and YouTube.</h3>
                <div style={{ marginTop: 20 }}>
                  <label>ChangePassword</label>
                  <button onClick={() => editModalOnClick()} className='button-class'>
                    <i className="fa fa-arrow-right" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Modal
          visible={showModal}
          okText={'Save'}
          onCancel={closeModalOnClick}
          title=" ChangePassword "
          width={800}
          onOk={saveOnClick}
        >
          <Spin spinning={loader} tip={'Loading...'}>
            <div className='row'>
              <div className="col-md-12">
                <div className="form-group">
                  <label>Username<span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type="text"
                    value={Username}
               //     defaultValue="qcadmin"
                    placeholder="qcadmin"
                    onChange={(e) => UsernameOnChange(e.target.value)}
                  />
                  {errors['Username'] &&
                    <span style={{ color: 'red' }}>{errors['Username']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>OldPassword <span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type={'password'}
                    name={'OldPassword'}
                    value={OldPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => OldPasswordOnChange(e.target.value)}
                  />
                  {errors['OldPassword'] &&
                    <span style={{ color: 'red' }}>{errors['OldPassword']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>NewPassword <span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type={'password'}
                    name={'NewPassword'}
                    value={NewPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => NewPasswordOnChange(e.target.value)}
                  />
                  {errors['NewPassword'] &&
                    <span style={{ color: 'red' }}>{errors['NewPassword']}</span>
                  }
                </div>
                <div className="form-group">
                  <label>ConfirmPassword<span style={{ color: 'red' }}>*</span></label>
                  <input
                    className="form-control"
                    type={'password'}
                    name={'ConfirmPassword'}
                    value={ConfirmPassword}
                    onBlur={valiadateProperty}
                    placeholder=""
                    onChange={(e) => ConfirmPasswordOnChange(e.target.value)}
                  />
                  {errors['ConfirmPassword'] &&
                    <span style={{ color: 'red' }}>{errors['ConfirmPassword']}</span>
                  }
                </div>
              </div>
            </div>

          </Spin>
        </Modal>
      </Spin>
    </div>
  );
};


export default Home;
