import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Spin, notification, Pagination, Tabs, Select } from 'antd';
import Link from 'next/link';
import Button from '@material-ui/core/Button';

import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableExams from '../../../components/tables/TableExams';

import { getQuery } from '../../../helper/auth';
import { getActiveExams, getInactiveExams } from '../../../store/exams/action';
import QuestionRepository from '../../../repositories/QuestionRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();

    const { auth } = useSelector(({ auth }) => auth);
    const {
        allExaminationCalendary,
        totalActiveCount,
        inactiveExaminationCalendar,
        totalInactiveCount,
    } = useSelector(({ Exams }) => Exams);

    const [mainCatId, setMainCatId] = useState('');
    const [mainCatName, setMainCatName] = useState('');
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCatIds, setSelectedCatIds] = useState([]);
    const [action, setAction] = useState('');

    const [search, setSearch] = useState('');



    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        let query = getQuery();
        setLoader(true);
        let ctr = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            mainExamId: query.exam_id,
            status: "Y"
        };
           console.log(ctr,"12345j")
        let ctr1 = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            mainExamId: query.exam_id,
            status: "N"
        };
       
        setMainCatId(query.exam_id);
        setMainCatName(query.exam_name);
        dispatch(getActiveExams(ctr));
        dispatch(getInactiveExams(ctr1));
        let tab = query.status === 'Y' ? 'active'  : 'inactive';
        setTab(tab);
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allExaminationCalendary]);

    const editModalOnClick = async (data) => {
        setLoader(true);
        setName(data.exam_name);
        setPosition(data.exam_pos)
        setSlug(data.exam_slug)
        setLoader(false);
        setShowModal(true);
    }
    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {
            _start: page === 1 ? 0 : ((page - 1) * pageSize),
            _limit: pageSize,
            exam_id: mainCatId,
        };
        if (search) {
            ctr.search = search;
        }
       
       
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedCatIds([]);
        setSelectAll(false);
        setAction('');
        setSearch('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allExaminationCalendary.map(h => h.exam_id);
            } else {
                array = inactiveExaminationCalendar.map(h => h.exam_id);
            }
        }
        setSelectedCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCatIds];
        let array1 = tab === 'active' ? [...allExaminationCalendary] : tab === 'inactive' ? [...waitingQuestions] : [...inactiveExaminationCalendar];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedCatIds(array);
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedCatIds];
        let obj = {
            qid: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "delete") {
                obj['status'] = 'D';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Deleted Successfully.'
                });
            }
            setSelectedCatIds([]);
            let ctr = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                exam_id: mainCatId,
                status: "Y"
            };
            let ctr1 = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                exam_id: mainCatId,
                status: "N"
            };
            if (search) {
                ctr.search = search;
                ctr1.search = search;
            }
          
          
         
            dispatch(getActiveExams(ctr));
            dispatch(getInactiveExams(ctr1));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    const searchOnChange = (search) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        ctr['search'] = search;
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setSearch(search);
        setCurrentPage(1);
    }

    const resetOnClick = () => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            exam_id: mainCatId,
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveExams(ctr));
        }  else {
            ctr['status'] = 'N';
            dispatch(getInactiveExams(ctr));
        }
        setSearch('');
        setCurrentPage(1);
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Examination Calendar'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs activeKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <Button
                                            variant="contained"
                                            onClick={goOnClick}
                                            style={{
                                                backgroundColor: "#cd4040",
                                                color: '#fff',
                                                height: 30
                                            }}
                                            className="jr-btn"
                                        >
                                            Go
                                        </Button>
                                    </div>
                                    <div className="col-md-2 text-right">
                                            <a>
                                                <Button
                                                    variant="contained"
                                                    style={{
                                                        backgroundColor: "#80bc00",
                                                        color: '#fff',
                                                        height: 30
                                                    }}
                                                    className="jr-btn"
                                                >
                                                    <i className="fas fa-plus" />Add
                                                </Button>
                                            </a>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <input
                                                className="form-control"
                                                type="text"
                                                placeholder="Search"
                                                style={{ height: 33 }}
                                                value={search}
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Button
                                                variant="contained"
                                                onClick={resetOnClick}
                                                style={{
                                                    backgroundColor: "#2196f3",
                                                    color: '#fff',
                                                    height: 30
                                                }}
                                                className="jr-btn"
                                            >
                                                <i className="fas fa-align-center" /> Reset
                                            </Button>
                                        </div>
                                    </div>
                                </div>
                               
                                <div style={{ marginTop: 10 }}>
                                    <TableExams
                                        calendarData={tab === "active" ? allExaminationCalendary : tab === "waiting" ? waitingQuestions : inactiveExaminationCalendar}
                                        editModalOnClick={editModalOnClick}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCatIds={selectedCatIds}
                                        mainCatId={mainCatId}
                                        mainCatName={mainCatName}
                                    />
                                </div>
                                <div style={{ margin: '10px auto', textAlign: 'right' }}>
                                    <Pagination
                                        total={tab === "active" ? allExaminationCalendary : tab === "waiting" ? waitingQuestionCount : totalInactiveCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Spin>
        </div>
    );
};


export default Home;
