import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllTestimonialsSuccess, getInactiveTestimonialsSuccess } from './action';

import DashboardCategoryRepository from '../../repositories/DashboardCategoryRepository';

function* getAllDashboardCategorySaga({ payload }) {
    try {
        const data = yield call(DashboardCategoryRepository.getAlldashboardCategory, payload);
        yield put(getAllTestimonialsSuccess(data));
    } catch (err) {
        yield put(getAllTestimonialsSuccess(null));
    }
}

function* getInactiveDashboardCategorySaga({ payload }) {
    try {
        const data = yield call(DashboardCategoryRepository.getInactivedashboardCategory, payload);
        yield put(getInactiveTestimonialsSuccess(data));
    } catch (err) {
        yield put(getInactiveTestimonialsSuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_TESTIMONIALS_REQUEST, getAllDashboardCategorySaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_TESTIMONIALS_REQUEST, getInactiveDashboardCategorySaga)]);
}
