                                        className="form-control"
                                        type="text"
                                        value={slug}
                                        placeholder=""
                                        onChange={(e) => slugOnChange(e.target.value)}
                                    />
                                    {errors['slug'] &&
                                        <span style={{ color: 'red' }}>{errors['slug']}</span>
                                    }
                                </div>
