import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Button, Checkbox, Spin, notification, Pagination, Tabs, Select } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableState from '../../../components/tables/State';
import { getAllState, getInactiveState } from '../../../store/State/action';
import LocationControllerRepository from '../../../repositories/LocationControllerRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const { allState, totalActiveCount, inactiveState, totalInactiveCount } = useSelector(({ State }) => State);
    const [showModal, setShowModal] = useState(false);
    const [statename, setStatename] = useState('');
    const [selectedHomeCatId, setSelectedHomeCatId] = useState('');
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedHomeCatIds, setSelectedHomeCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [Country, setCountry] = useState('');
    const [countrycode, setCountrycode] = useState('');
    const [search, setsearch] = useState('');

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllState(ctr));
        dispatch(getInactiveState(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allState, inactiveState]);

    const addModalOnClick = async () => {
        setLoader(true);
        setStatename('');
        setCountrycode('');
        setCountry();
        setSelectedHomeCatId('');
        setLoader(false);
        setShowModal(true);
    }

    const editModalOnClick = async (data) => {
        setLoader(true);
        setSelectedHomeCatId(data.state_id);
        setCountry(data.country);
        setStatename(data.state_name)
        setCountrycode(data.country_code)
        setLoader(false);
        setShowModal(true);
    }

    const closeModalOnClick = () => {
        setStatename('');
        setCountrycode('');
        setSelectedHomeCatId('');
        setCountry('');
        setErrors({});
        setShowModal(false);
    }

    const CountryOnChange = (Country) => {
        let errorObj = { ...errors };
        errorObj['Country'] = '';
        setCountry(Country);
        setErrors(errorObj);
    }

    const statenameOnChange = (statename) => {
        let errorObj = { ...errors };
        errorObj['statename'] = '';
        setStatename(statename);
        setErrors(errorObj);
    }
    const countrycodeOnChange = (countrycode) => {
        let errorObj = { ...errors };
        errorObj['countrycode'] = '';
        setCountrycode(countrycode);
        setErrors(errorObj);
    }
    const saveOnClick = () => {
        if (selectedHomeCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (statename && selectedHomeCatId) {
            setLoader(true);
            let obj = {
                "country_id": selectedHomeCatId,
                "state_name": statename,
                "country_code": countrycode
            }
            try {
                await LocationControllerRepository.saveState(obj);
                notification.success({
                    message: 'State Added Successfully.'
                })
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;

                if (search) {
                    ctr.search = search;
                }
                dispatch(getAllState(ctr));
                dispatch(getInactiveState(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!statename) errorObj['statename'] = "Please Enter statename";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (statename && selectedHomeCatId) {
            setLoader(true);
            let obj = {
                "country_id": selectedHomeCatId,
                "state_name": statename,
                "country_code": countrycode
            }
            try {
                await LocationControllerRepository.editState(selectedHomeCatId, obj);
                notification.success({
                    message: 'State Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                dispatch(getAllState(ctr));
                dispatch(getInactiveState(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'State  Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!statename) errorObj['statename'] = "Please Enter statename";
            setErrors(errorObj);
        }
    }

    const searchOnChange = (search) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.search = search;
        if (search) {
            ctr.search = search;
        }
        if (tab === 'active') {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setsearch(search);
        setCurrentPage(1);
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;

        if (tab === "active") {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllState(ctr));
        } else {
            dispatch(getInactiveState(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedHomeCatIds([]);
        setSelectAll(false);
        setAction('');
        setsearch('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allState.map(h => h.state_id);
            } else {
                array = inactiveState.map(h => h.state_id);
            }
        }
        setSelectedHomeCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedHomeCatIds];
        let array1 = tab === 'active' ? [...allState] : [...inactiveState];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedHomeCatIds(array);
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedHomeCatIds];
        let obj = {
            stateId: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await LocationControllerRepository.inactiveCategory(obj);
                notification.success({
                    message: 'State  Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await LocationControllerRepository.inactiveCategory(obj);
                notification.success({
                    message: 'State  Updated Successfully.'
                });
            }
            if (action === "delete") {
                obj['status'] = 'D';
                await LocationControllerRepository.inactiveCategory(obj);
                notification.success({
                    message: 'State  Deleted Successfully.'
                });
            }
            setSelectedHomeCatIds([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;

            if (search) {
                ctr.search = search;
            }
            dispatch(getAllState(ctr));
            dispatch(getInactiveState(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'State'} />
                            </div>
                        </div>
                    {/* //////setting start/////// */}
                    </div>
                </div>
            </Spin>
        </div>
    );
};


export default Home;
