import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllQBankMainCategorySuccess, getInactiveQBankMainCategorySuccess } from './action';

import QBankMainCategoryRepository from '../../repositories/QBankMainCategoryRepository';

function* getAllQBankMainCategorySaga({ payload }) {
    try {
        const data = yield call(QBankMainCategoryRepository.getAllQBankMainCategory, payload);
        yield put(getAllQBankMainCategorySuccess(data));
    } catch (err) {
        yield put(getAllQBankMainCategorySuccess(null));
    }
}

function* getInactiveQBankMainCategorySaga({ payload }) {
    try {
        const data = yield call(QBankMainCategoryRepository.getInactiveQBankMainCategory, payload);
        yield put(getInactiveQBankMainCategorySuccess(data));
    } catch (err) {
        yield put(getInactiveQBankMainCategorySuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_QBANK_MAIN_CATEGORY_REQUEST, getAllQBankMainCategorySaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_QBANK_MAIN_CATEGORY_REQUEST, getInactiveQBankMainCategorySaga)]);
}
