import React from 'react';
import { Checkbox, Table } from 'antd';
import DashboardCategoryRepository from '../../../repositories/DashboardCategoryRepository';
const TableDashboardCategory = ({ allDashboardCategory, editModalOnClick, onSelectAll, onSelectOne, selectAll, selectedHomeCatIds, mainPositionOnChange, posotionChangeCategorys }) => {
  const columns = [
    {
      title: 'Category',
      dataIndex: 'category',
    },
    {
      title: 'Position',
      dataIndex: 'position',
    },
    {
      title: 'Edit',
      dataIndex: 'edit',
    },
    {
      title: 'Delete',
      dataIndex: 'delete',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check'
    }
  ];
  const deleteOneDashboardCategory = (id) => {
         DashboardCategoryRepository.deleteCategory(id);
        notification.success({
            message: 'Home Category Deleted Successfully.'
        });
    
  }
  let data = allDashboardCategory.map(a => {
    let position = posotionChangeCategorys.filter(p => p.catId === a.cat_name);
    let obj = {
      key: a.cat_id,
      category: a.cat_name,
      position: (
        <input
          type="text"
          onChange={event => mainPositionOnChange(a.cat_id, event.target.value)}
          style={{ width: '50px', textAlign: 'center', padding: '0px' }}
          value={position && position.length > 0 ? position[0].position : a.cat_pos}
          className="form-control form-control-lg"
        />
      ),
      edit: (<i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>),
      delete : (<i className="fa fa-trash" onClick={() => deleteOneDashboardCategory(obj)} style={{cursor: 'pointer'}}></i>),

      check: (
        <Checkbox
          onClick={() => onSelectOne(a.cat_id)}
          checked={selectedHomeCatIds.indexOf(a.cat_id) >= 0}
        />
      )
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableDashboardCategory;
