import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { Modal, Spin, notification } from 'antd';
import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import adminService from '../../../repositories/AdminRepository';
import Joi from 'joi-browser';


const Home = (props) => {
    const { auth } = useSelector(({ auth }) => auth);
    const [showModal, setShowModal] = useState(true);
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [Username, setUsername] = useState('');
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [userType, setUserType] = useState("");
    const [userLoginType, setUserLoginType] = useState("");

    const schema = {
        OldPassword: Joi.string().required(),
        NewPassword: Joi.string().min(5).required(),
        ConfirmPassword: Joi.string().required(),
    }

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    const closeModalOnClick = () => {
        window.location.href = "/home";
        setShowModal(false);
    }

    const UsernameOnChange = (Username) => {
        let errorObj = { ...errors };
        errorObj['Username'] = '';
        setUsername(Username);
        setErrors(errorObj);
    }

    const OldPasswordOnChange = (oldPassword) => {
        let errorObj = { ...errors };
        errorObj['OldPassword'] = '';
        setOldPassword(oldPassword);
        setErrors(errorObj);
    }

    const NewPasswordOnChange = (newPassword) => {
        let errorObj = { ...errors };
        errorObj['NewPassword'] = '';
        setNewPassword(newPassword);
        setErrors(errorObj);
    }

    const ConfirmPasswordOnChange = (confirmPassword) => {
        let errorObj = { ...errors };
        errorObj['ConfirmPassword'] = '';
        setConfirmPassword(confirmPassword);
        setErrors(errorObj);
    }

    const saveOnClick = () => {
        changePassword();
    }
    const imageOnChange = async (file) => {
        let errorObj = { ...errors };
        setBttonLoader(true);
        setImage('');
        const formData = new FormData();
        formData.append('dashboard', file);
        let result = await DashboardCategoryRepository.imageUpload(formData);
        if (result && result.statusCode === 200) {
            setImage(result.data);
            setBttonLoader(false);
        }
        errorObj['CatImage'] = '';
        setErrors(errorObj);
    }
    const handleRefresh = async () => {
        setLoader(true);
        setOldPassword('');
        setNewPassword('');
        setConfirmPassword('');
        setUsername(auth.name);
        setUserType(auth.type);
        setUserLoginType(auth.logintype);
        setLoader(false);
    }

    useEffect(() => {
        handleRefresh()
    }, [])

    const valiadateProperty = (e) => {
        let { name, value, className } = e.currentTarget;
        const obj = { [name]: value };
        const filedSchema = { [name]: schema[name] };
        const { error } = Joi.validate(obj, filedSchema);

        let message = error ? error.details[0].message : null;
        setErrors({ ...errors, [name]: message, "errordetails": null })
    }

    const editModalOnClick = async (data) => {
        console.log(data, "data")
        setLoader(true);
        setUsername(auth.name)
        setOldPassword('')
        setConfirmPassword('')
        setNewPassword('')
        setUserType(auth.type);
        setUserLoginType(auth.logintype);
        setLoader(false);
        setShowModal(true);
    }

    const changePassword = async () => {
        if (newPassword != confirmPassword) {
            setErrors({ ...errors, ['ConfirmPassword']: 'Confirm Password must match New Password', "errordetails": null })
        } else {
            if (oldPassword && newPassword && confirmPassword && errors['ConfirmPassword'] === null &&
                errors['NewPassword'] === null && errors['OldPassword'] === null) {
                let obj = {
                    oldpassword: oldPassword,
                    newpassword: newPassword,
                    type: userType,
                    logintype: userLoginType
                };

                try {
                    console.log(obj, "qq3");
                    await adminService.changePassword(obj);
                    notification.success({
                        message: 'Password Changed Successfull'
                    })

                } catch (err) {
                    notification.error({
                        message: 'Password Changed Failed.'
                    });
                }
            } else {
                let errorObj = { ...errors };
                if (!oldPassword) errorObj['OldPassword'] = "Please Enter OldPassword";
                if (!newPassword) errorObj['NewPassword'] = "Please Enter newPassword";
                if (!confirmPassword) errorObj['ConfirmPassword'] = "Please Enter ConfirmPassword";
                setErrors(errorObj);
            }
        }
    }


    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Settings'} />
                            </div>
                        </div>
                        <Modal
                            visible={showModal}
                            okText={'Save'}
                           onCancel={closeModalOnClick}
                            title=" Admin Settings "
                            width={800}
                            onOk={saveOnClick}
                        >
                            <Spin spinning={loader} tip={'Loading...'}>
                                <div className='row'>
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label>Site Name:<span style={{ color: 'red' }}>*</span></label>
                                            <input
                                                className="form-control"
                                                type="text"
                                                value={Username}
                                                placeholder="qcadmin"
                                                onChange={(e) => UsernameOnChange(e.target.value)}
                                            />
                                            {errors['Username'] &&
                                                <span style={{ color: 'red' }}>{errors['Username']}</span>
                                            }
                                        </div>
                                        <div className="form-group">
                                            <label>Support Email: <span style={{ color: 'red' }}>*</span></label>
                                            <input
                                                className="form-control"
                                                type={'password'}
                                                name={'OldPassword'}
                                                value={oldPassword}
                                                onBlur={valiadateProperty}
                                                placeholder=""
                                                onChange={(e) => OldPasswordOnChange(e.target.value)}
                                            />
                                            {errors['OldPassword'] &&
                                                <span style={{ color: 'red' }}>{errors['OldPassword']}</span>
                                            }
                                        </div>
                                        <div className="form-group">
                                            <label>Support Phone:<span style={{ color: 'red' }}>*</span></label>
                                            <input
                                                className="form-control"
                                                type={'password'}
                                                name={'NewPassword'}
                                                value={newPassword}
