import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Spin, notification, Pagination, Tabs, Select } from 'antd';

import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableQBankSubCategory from '../../../components/tables/TableExaminationCalendar';

import { getAllQBankSubCategory, getInactiveQBankSubCategory } from '../../../store/qBankSubCategory/action';
import ExaminationCalendarRepository from '../../../repositories/ExaminationCalendarRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();
    const { auth } = useSelector(({ auth }) => auth);
    const {
        activeQBankMainCategory,
        inactiveQBankMainCategory,
        allQBankSubCategory,
        inactiveQBankSubCategory,
        totalActiveCount,
        totalInactiveCount,
        waitingQuestionCountActive,
        activeQuestionCountActive,
        waitingQuestionCountInactive,
        activeQuestionCountInactive,
        totalActiveSubCatCount,
        totalInactiveSubCatCount
    } = useSelector(({ qBankSubCategory }) => qBankSubCategory);

    const [showModal, setShowModal] = useState(false);
    const [mainCatId, setMainCatId] = useState('');
    const [name, setName] = useState('');
    const [uniqueCode, setUniqueCode] = useState('');
    const [description, setDescription] = useState('');
    const [selectedCatId, setSelectedCatId] = useState('');
    const [errors, setErrors] = useState({});
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('active');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCatIds, setSelectedCatIds] = useState([]);
    const [action, setAction] = useState('');
    const [posotionChangeCategorys, setPosotionChangeCategorys] = useState([]);
    const [search, setSearch] = useState('');
    const [searchMainCat, setSearchMainCat] = useState('');

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        setLoader(true);
        let ctr = {};
        ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
        ctr._limit = pageSizeTotal;
        dispatch(getAllQBankSubCategory(ctr));
        dispatch(getInactiveQBankSubCategory(ctr));
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [allQBankSubCategory, inactiveQBankSubCategory]);

    const addModalOnClick = async () => {
        setLoader(true);
        setName('');
        setUniqueCode('');
        setDescription('');
        setSelectedCatId('');
        setMainCatId('');
        setLoader(false);
        setShowModal(true);
    }

    const editModalOnClick = async (data) => {
        setLoader(true);
        setName(data.cat_name);
        setUniqueCode(data.cat_code);
        setDescription(data.cat_desc);
        setSelectedCatId(data.cat_id);
        setMainCatId(data.pid);
        setLoader(false);
        setShowModal(true);
    }

    const closeModalOnClick = () => {
        setName('');
        setUniqueCode('');
        setDescription('');
        setSelectedCatId('');
        setMainCatId('');
        setErrors({});
        setShowModal(false);
    }

    const mainCategoryChange = (value) => {
        let errorObj = { ...errors };
        setMainCatId(value);
        errorObj['mainCatId'] = '';
        setErrors(errorObj);
    }

    const nameOnChange = (name) => {
        let errorObj = { ...errors };
        errorObj['name'] = '';
        setName(name);
        setErrors(errorObj);
    }

    const uniqueCodeOnChange = (code) => {
        let errorObj = { ...errors };
        setUniqueCode(code);
        errorObj['uniqueCode'] = '';
        setErrors(errorObj);
    }

    const descriptionOnChange = (description) => {
        setDescription(description);
    }

    const saveOnClick = () => {
        if (selectedCatId) {
            edit();
        } else {
            save();
        }
    }

    const save = async () => {
        if (name && uniqueCode && mainCatId) {
            setLoader(true);
            const saveObj = {};
            saveObj.cat_name = name;
            saveObj.cat_code = uniqueCode;
            saveObj.cat_desc = description;
            saveObj.pid = mainCatId;
            try {
                await ExaminationCalendarRepository.saveQBankSubCategory(saveObj);
                notification.success({
                    message: 'Q Bank Sub Category Added Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                if (searchMainCat) {
                    ctr.searchMainCat = searchMainCat;
                }
                if (search) {
                    ctr.search = search;
                }
                dispatch(getAllQBankSubCategory(ctr));
                dispatch(getInactiveQBankSubCategory(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Q Bank Sub Category Added Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!name) errorObj['name'] = "Please Enter Sub Category Name";
            if (!uniqueCode) errorObj['uniqueCode'] = "Please Enter Unique Code";
            if (!mainCatId) errorObj['mainCatId'] = "Please Select Main Category";
            setErrors(errorObj);
        }
    }

    const edit = async () => {
        if (name && uniqueCode && mainCatId) {
            setLoader(true);
            const editObj = {};
            editObj.cat_name = name;
            editObj.cat_code = uniqueCode;
            editObj.cat_desc = description;
            editObj.pid = mainCatId;
            try {
                await ExaminationCalendarRepository.editQBankSubCategory(selectedCatId, editObj);
                notification.success({
                    message: 'Q Bank Sub Category Updated Successfully.'
                });
                let ctr = {};
                ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
                ctr._limit = pageSizeTotal;
                if (searchMainCat) {
                    ctr.searchMainCat = searchMainCat;
                }
                if (search) {
                    ctr.search = search;
                }
                dispatch(getAllQBankSubCategory(ctr));
                dispatch(getInactiveQBankSubCategory(ctr));
                closeModalOnClick();
            } catch (e) {
                notification.error({
                    message: 'Q Bank Main Category Updated Failed.'
                });
            }
        } else {
            let errorObj = { ...errors };
            if (!name) errorObj['name'] = "Please Enter Sub Category Name";
            if (!uniqueCode) errorObj['uniqueCode'] = "Please Enter Unique Code";
            if (!mainCatId) errorObj['mainCatId'] = "Please Select Main Category";
            setErrors(errorObj);
        }
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {};
        ctr._start = page === 1 ? 0 : ((page - 1) * pageSize);
        ctr._limit = pageSize;
        if (searchMainCat) {
            ctr.searchMainCat = searchMainCat;
        }
        if (search) {
            ctr.search = search;
        }
        if (tab === "active") {
            dispatch(getAllQBankSubCategory(ctr));
        } else {
            dispatch(getInactiveQBankSubCategory(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = 10;
        if (tab === "active") {
            dispatch(getAllQBankSubCategory(ctr));
        } else {
            dispatch(getInactiveQBankSubCategory(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedCatIds([]);
        setSelectAll(false);
        setAction('');
        setPosotionChangeCategorys([]);
        setSearch('');
        setSearchMainCat('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = allQBankSubCategory.map(h => h.cat_id);
            } else {
                array = inactiveQBankSubCategory.map(h => h.cat_id);
            }
        }
        setSelectedCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCatIds];
        let array1 = tab === 'active' ? [...allQBankSubCategory] : [...inactiveQBankSubCategory];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedCatIds(array);
    }

    const mainPositionOnChange = (id, position) => {
        let array = [...posotionChangeCategorys];
        const re = /^[0-9\b]+$/; //rules
        if (position === "" || re.test(position)) {
            let index = array.findIndex(a => a.catId === id);
            if (index >= 0) {
                array[index]['position'] = position;
            } else {
                array.push({
                    catId: id,
                    position: position
                });
            }
            setPosotionChangeCategorys(array);
        }
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedCatIds];
        let obj = {
            catId: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await ExaminationCalendarRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Q Bank Sub Category Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await ExaminationCalendarRepository.inactiveCategory(obj);
                notification.success({
                    message: 'Q Bank Sub Category Updated Successfully.'
                });
            }
            if (action === "delete") {
                await ExaminationCalendarRepository.deleteCategory(obj);
                notification.success({
                    message: 'Q Bank Sub Category Deleted Successfully.'
                });
            }
            if (action === "position") {
                let array = [...posotionChangeCategorys];
                array = array.filter(a => selectedHomeCatIdsArr.indexOf(a.catId) >= 0);
                await ExaminationCalendarRepository.changePosition({ values: array });
                notification.success({
                    message: 'Q Bank Sub Category Updated Successfully.'
                });
            }
            setSelectedCatIds([]);
            setPosotionChangeCategorys([]);
            let ctr = {};
            ctr._start = currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal);
            ctr._limit = pageSizeTotal;
            if (searchMainCat) {
                ctr.searchMainCat = searchMainCat;
            }
            if (search) {
                ctr.search = search;
            }
            dispatch(getAllQBankSubCategory(ctr));
            dispatch(getInactiveQBankSubCategory(ctr));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    const searchOnChange = (search) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.search = search;
        if (searchMainCat) {
            ctr.searchMainCat = searchMainCat;
        }
        if (tab === 'active') {
            dispatch(getAllQBankSubCategory(ctr));
        } else {
            dispatch(getInactiveQBankSubCategory(ctr));
        }
        setSearch(search);
        setCurrentPage(1);
    }

    const searchMainCatOnChange = (searchMainCat) => {
        setLoader(true);
        let ctr = {};
        ctr._start = 0;
        ctr._limit = pageSizeTotal;
        ctr.searchMainCat = searchMainCat;
        if (search) {
            ctr.search = search;
        }
        if (tab === 'active') {
            dispatch(getAllQBankSubCategory(ctr));
        } else {
            dispatch(getInactiveQBankSubCategory(ctr));
        }
        setSearchMainCat(searchMainCat);
        setCurrentPage(1);
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Q Bank Sub Category'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs defaultActiveKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Active ${totalActiveCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                <Option value="position">Position</Option>
                                                <Option value="delete">Delete</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <button onClick={goOnClick} style={{ backgroundColor: '#cd4040', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            Go
                                        </button>
                                    </div>
                                    <div className="col-md-6 text-right">
                                        <button onClick={addModalOnClick} style={{ backgroundColor: '#80bc00', width: 100, height: 35, color: '#fff', border: 'none' }}>
                                            <i className="fas fa-plus" /> Add
                                        </button>
                                    </div>
                                </div>
                                <div className='row'>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <Select
                                                onChange={searchMainCatOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: '100%' }}
                                                value={searchMainCat}
                                                showSearch={true}
                                                filterOption={(input, option) =>
                                                    option.children.toString().toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                }
                                            >
                                                <Option value="">Select Main Category</Option>
                                                {activeQBankMainCategory.map(m => {
                                                    return (
                                                        <Option value={m.cat_id} key={m.cat_id}>{m.cat_name}</Option>
                                                    )
                                                })}
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <input
                                                className="form-control"
                                                type="text"
                                                placeholder="Search"
                                                value={search}
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 10 }}>
                                    <TableQBankSubCategory
                                        category={tab === "active" ? allQBankSubCategory : inactiveQBankSubCategory}
                                        mainCategory={tab === "active" ? activeQBankMainCategory : inactiveQBankMainCategory}
                                        editModalOnClick={editModalOnClick}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCatIds={selectedCatIds}
                                        mainPositionOnChange={mainPositionOnChange}
                                        posotionChangeCategorys={posotionChangeCategorys}
                                        waitingQuestionCount={tab === "active" ? waitingQuestionCountActive : waitingQuestionCountInactive}
                                        activeQuestionCount={tab === "active" ? activeQuestionCountActive : activeQuestionCountInactive}
                                    />
                                </div>
                                <div style={totalActiveCount > 0 ? { margin: '10px auto', textAlign: 'right' } : { display: 'none' }}>
                                    <Pagination
                                        total={tab === "active" ? totalActiveSubCatCount : totalInactiveSubCatCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <Modal
                    visible={showModal}
                    okText={'Save'}
                    onCancel={closeModalOnClick}
                    title={selectedCatId ? "Edit Q Bank Sub Category" : "Add New Q Bank Sub Category"}
                    width={800}
                    onOk={saveOnClick}
                >
                    <Spin spinning={loader} tip={'Loading...'}>
                        <div className='row'>
                            <div className="col-md-12">
                                <div className="form-group">
                                    <label>Main Category <span style={{ color: 'red' }}>*</span></label>
                                    <Select
                                        onChange={mainCategoryChange}
                                        placeholder="Select Action"
                                        className="ps-ant-dropdown"
                                        style={{ width: '100%' }}
                                        value={mainCatId}
                                        showSearch={true}
                                        filterOption={(input, option) =>
                                            option.children.toString().toLowerCase().indexOf(input.toLowerCase()) >= 0
                                        }
                                    >
                                        <Option value="">Select</Option>
                                        {activeQBankMainCategory.map(m => {
                                            return (
                                                <Option value={m.cat_id} key={m.cat_id}>{m.cat_name}</Option>
                                            )
                                        })}
                                    </Select>
                                    {errors['mainCatId'] &&
                                        <span style={{ color: 'red' }}>{errors['mainCatId']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Sub Category Name <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={name}
                                        placeholder=""
                                        onChange={(e) => nameOnChange(e.target.value)}
                                    />
                                    {errors['name'] &&
                                        <span style={{ color: 'red' }}>{errors['name']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Unique Code <span style={{ color: 'red' }}>*</span></label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={uniqueCode}
                                        placeholder=""
                                        onChange={(e) => uniqueCodeOnChange(e.target.value)}
                                    />
                                    {errors['uniqueCode'] &&
                                        <span style={{ color: 'red' }}>{errors['uniqueCode']}</span>
                                    }
                                </div>
                                <div className="form-group">
                                    <label>Description</label>
                                    <input
                                        className="form-control"
                                        type="text"
                                        value={description}
                                        placeholder=""
                                        onChange={(e) => descriptionOnChange(e.target.value)}
                                    />
                                </div>
                            </div>
                        </div>
                    </Spin>
                </Modal>
            </Spin>
        </div>
    );
};


export default Home;
