import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Modal, Spin, notification, Pagination, Tabs, Select } from 'antd';
import Link from 'next/link';
import Button from '@material-ui/core/Button';

import HeaderDashboard from '../../../components/header/HeaderDashboard';
import Sidebar from '../../../components/sections/sidebar';
import TableQuestions from '../../../components/tables/TableQuestions';

import { getQuery } from '../../../helper/auth';
import { getActiveQuestions, getInactiveQuestions, getWaitingQuestions } from '../../../store/questions/action';
import QuestionRepository from '../../../repositories/QuestionRepository';
import AdminRepository from '../../../repositories/AdminRepository';
import QBankSubCategoryRepository from '../../../repositories/QBankSubCategoryRepository';

const Home = (props) => {

    const { TabPane } = Tabs;
    const { Option } = Select;
    const dispatch = useDispatch();

    const { auth } = useSelector(({ auth }) => auth);
    const {
        totalActiveQuestionCount,
        totalInactiveQuestionCount,
        totalWaitingQuestionCount,
        activeQuestions,
        inactiveQuestions,
        waitingQuestions,
        activeQuestionCount,
        inactiveQuestionCount,
        waitingQuestionCount
    } = useSelector(({ questions }) => questions);

    const [mainCatId, setMainCatId] = useState('');
    const [subCatId, setSubCatId] = useState('');
    const [mainCatName, setMainCatName] = useState('');
    const [subCatName, setSubCatName] = useState('');
    const [loader, setLoader] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSizeTotal, setPageSizeTotal] = useState(10);
    const [tab, setTab] = useState('');
    const [selectAll, setSelectAll] = useState(false);
    const [selectedCatIds, setSelectedCatIds] = useState([]);
    const [action, setAction] = useState('');

    const [fucaltyList, setFucaltyList] = useState([]);
    const [search, setSearch] = useState('');
    const [searchType, setSearchType] = useState('');
    const [sortDate, setSortDate] = useState('');
    const [searchLevel, setSearchLevel] = useState('');
    const [searchFucalty, setSearchFucalty] = useState('');

    const [qBankMainCat, setQBankMainCat] = useState([]);
    const [qBankSubCat, setQBankSubCat] = useState([]);
    const [selectedQBankMainCat, setSelectedQBankMainCat] = useState('');
    const [selectedQBankSubCat, setSelectedQBankSubCat] = useState('');

    useEffect(() => {
        let local = JSON.parse(localStorage.getItem('persist:QuestionCloudAdmin'));
        let localAuth = local && local.auth ? JSON.parse(local.auth) : {};
        if (localAuth && !localAuth.isLoggedIn) {
            window.location.href = "/";
        }
    }, [auth]);

    useEffect(() => {
        let query = getQuery();
        getFucaltyList();
        getQBankCategory();
        setLoader(true);
        let ctr = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            cat_id: query.mainId,
            sub_id: query.subId,
            status: "Y"
        };
        let ctr1 = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            cat_id: query.mainId,
            sub_id: query.subId,
            status: "N"
        };
        let ctr2 = {
            _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
            _limit: pageSizeTotal,
            cat_id: query.mainId,
            sub_id: query.subId,
            status: "W"
        };
        setMainCatId(query.mainId);
        setSubCatId(query.subId);
        setMainCatName(query.mainName);
        setSubCatName(query.subName);
        dispatch(getActiveQuestions(ctr));
        dispatch(getInactiveQuestions(ctr1));
        dispatch(getWaitingQuestions(ctr2));
        let tab = query.status === 'Y' ? 'active' : query.status === 'W' ? 'waiting' : 'inactive';
        setTab(tab);
    }, []);

    useEffect(() => {
        setLoader(false);
    }, [activeQuestions, inactiveQuestions, waitingQuestions]);

    const getFucaltyList = async () => {
        let result = await AdminRepository.getAllOperators();
        setFucaltyList(result.Operator);
    }

    const getQBankCategory = async () => {
        let result = await QBankSubCategoryRepository.getAllQBankSubCategory({ _start: 0, _limit: 100000 });
        setQBankMainCat(result.category);
    }

    const pageSizeChange = async (page, pageSize) => {
        setLoader(true);
        let ctr = {
            _start: page === 1 ? 0 : ((page - 1) * pageSize),
            _limit: pageSize,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        if (search) {
            ctr.search = search;
        }
        if (searchType) {
            ctr['searchType'] = searchType;
        }
        if (sortDate) {
            ctr['sortDate'] = sortDate;
        }
        if (searchLevel) {
            ctr['searchLevel'] = searchLevel;
        }
        if (searchFucalty) {
            ctr['searchFucalty'] = searchFucalty;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setCurrentPage(page);
        setPageSizeTotal(pageSize);
    }

    const changeTab = (tab) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setCurrentPage(1);
        setPageSizeTotal(10);
        setSelectedCatIds([]);
        setSelectAll(false);
        setAction('');
        setSearch('');
        setSearchType('');
        setSortDate('');
        setSearchLevel('');
        setSearchFucalty('');
        setTab(tab);
    }

    const onSelectAll = (value) => {
        let array = [];
        if (value) {
            if (tab === 'active') {
                array = activeQuestions.map(h => h.qid);
            } else if (tab === 'waiting') {
                array = waitingQuestions.map(h => h.qid);
            } else {
                array = inactiveQuestions.map(h => h.qid);
            }
        }
        setSelectedCatIds(array);
        setSelectAll(value);
    }

    const onSelectOne = (id) => {
        let array = [...selectedCatIds];
        let array1 = tab === 'active' ? [...activeQuestions] : tab === 'waiting' ? [...waitingQuestions] : [...inactiveQuestions];
        let index = array.indexOf(id);
        if (index >= 0) {
            array.splice(index, 1);
        } else {
            array.push(id);
        }
        if (array.length === array1.length) {
            setSelectAll(true);
        } else {
            setSelectAll(false);
        }
        setSelectedCatIds(array);
    }

    const actionOnChange = (action) => {
        setAction(action);
    }

    const goOnClick = async () => {
        let selectedHomeCatIdsArr = [...selectedCatIds];
        let obj = {
            qid: selectedHomeCatIdsArr
        };
        if (selectedHomeCatIdsArr && selectedHomeCatIdsArr.length > 0 && action) {
            setLoader(true);
            if (action === "active") {
                obj['status'] = 'Y';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "inactive") {
                obj['status'] = 'N';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "waiting") {
                obj['status'] = 'W';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Updated Successfully.'
                });
            }
            if (action === "delete") {
                obj['status'] = 'D';
                await QuestionRepository.questionsStatusChange(obj);
                notification.success({
                    message: 'Quetions Deleted Successfully.'
                });
            }
            setSelectedCatIds([]);
            let ctr = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                cat_id: mainCatId,
                sub_id: subCatId,
                status: "Y"
            };
            let ctr1 = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                cat_id: mainCatId,
                sub_id: subCatId,
                status: "N"
            };
            let ctr2 = {
                _start: currentPage === 1 ? 0 : ((currentPage - 1) * pageSizeTotal),
                _limit: pageSizeTotal,
                cat_id: mainCatId,
                sub_id: subCatId,
                status: "W"
            };
            if (search) {
                ctr.search = search;
                ctr1.search = search;
                ctr2.search = search;
            }
            if (searchType) {
                ctr['searchType'] = searchType;
                ctr1['searchType'] = searchType;
                ctr2['searchType'] = searchType;
            }
            if (sortDate) {
                ctr['sortDate'] = sortDate;
                ctr1['sortDate'] = sortDate;
                ctr2['sortDate'] = sortDate;
            }
            if (searchLevel) {
                ctr['searchLevel'] = searchLevel;
                ctr1['searchLevel'] = searchLevel;
                ctr2['searchLevel'] = searchLevel;
            }
            if (searchFucalty) {
                ctr['searchFucalty'] = searchFucalty;
                ctr1['searchFucalty'] = searchFucalty;
                ctr2['searchFucalty'] = searchFucalty;
            }
            dispatch(getActiveQuestions(ctr));
            dispatch(getInactiveQuestions(ctr1));
            dispatch(getWaitingQuestions(ctr2));
        } else {
            if (!action) {
                Modal.error({
                    title: 'Please Select Action'
                });
            } else if (!selectedHomeCatIdsArr.length) {
                Modal.error({
                    title: 'Please Select One Category'
                });
            }
        }
    }

    const searchOnChange = (search) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        ctr['search'] = search;
        if (searchType) {
            ctr['searchType'] = searchType;
        }
        if (sortDate) {
            ctr['sortDate'] = sortDate;
        }
        if (searchLevel) {
            ctr['searchLevel'] = searchLevel;
        }
        if (searchFucalty) {
            ctr['searchFucalty'] = searchFucalty;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSearch(search);
        setCurrentPage(1);
    }

    const searchTypeOnChange = (type) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        ctr['searchType'] = type;
        if (search) {
            ctr['search'] = search;
        }
        if (sortDate) {
            ctr['sortDate'] = sortDate;
        }
        if (searchLevel) {
            ctr['searchLevel'] = searchLevel;
        }
        if (searchFucalty) {
            ctr['searchFucalty'] = searchFucalty;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSearchType(type);
        setCurrentPage(1);
    }

    const sortDateOnChange = (sortDate) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        ctr['sortDate'] = sortDate;
        if (searchType) {
            ctr['searchType'] = searchType;
        }
        if (search) {
            ctr['search'] = search;
        }
        if (searchLevel) {
            ctr['searchLevel'] = searchLevel;
        }
        if (searchFucalty) {
            ctr['searchFucalty'] = searchFucalty;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSortDate(sortDate);
        setCurrentPage(1);
    }

    const searchLevelOnChange = (level) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        ctr['searchLevel'] = level;
        if (searchType) {
            ctr['searchType'] = searchType;
        }
        if (search) {
            ctr['search'] = search;
        }
        if (sortDate) {
            ctr['sortDate'] = sortDate;
        }
        if (searchFucalty) {
            ctr['searchFucalty'] = searchFucalty;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSearchLevel(level);
        setCurrentPage(1);
    }

    const searchFucaltyOnChange = (fucalty) => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        ctr['searchFucalty'] = fucalty;
        if (searchType) {
            ctr['searchType'] = searchType;
        }
        if (search) {
            ctr['search'] = search;
        }
        if (sortDate) {
            ctr['sortDate'] = sortDate;
        }
        if (searchLevel) {
            ctr['searchLevel'] = searchLevel;
        }
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSearchFucalty(fucalty);
        setCurrentPage(1);
    }

    const resetOnClick = () => {
        setLoader(true);
        let ctr = {
            _start: 0,
            _limit: 10,
            cat_id: mainCatId,
            sub_id: subCatId
        };
        if (tab === "active") {
            ctr['status'] = 'Y';
            dispatch(getActiveQuestions(ctr));
        } else if (tab === "waiting") {
            ctr['status'] = 'W';
            dispatch(getWaitingQuestions(ctr));
        } else {
            ctr['status'] = 'N';
            dispatch(getInactiveQuestions(ctr));
        }
        setSearchFucalty('');
        setSearch('');
        setSearchLevel('');
        setSortDate('');
        setSearchType('');
        setCurrentPage(1);
    }

    return (
        <div>
            <Spin spinning={loader} tip={'Loading...'}>
                <HeaderDashboard />
                <div className="dashboard-container">
                    <div className="row no-gutters">
                        <div className="col-md-2">
                            <div id="sidebar">
                                <Sidebar page={'Q Bank Sub Category'} />
                            </div>
                        </div>
                        <div className="col-md-10">
                            <div className="content content-width" id="style-2">
                                <Tabs activeKey={tab} onChange={changeTab}>
                                    <TabPane tab={`Waiting ${totalWaitingQuestionCount}`} key="waiting">
                                    </TabPane>
                                    <TabPane tab={`Active ${totalActiveQuestionCount}`} key="active">
                                    </TabPane>
                                    <TabPane tab={`Inactive ${totalInactiveQuestionCount}`} key="inactive">
                                    </TabPane>
                                </Tabs>
                                <div className='row'>
                                    <div className="col-md-4">
                                        <div className="form-group">
                                            <Select
                                                onChange={actionOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: 200 }}
                                                value={action}
                                            >
                                                {tab === 'active' && <Option value="inactive">Inactive</Option>}
                                                {tab === 'active' && <Option value="waiting">Waiting</Option>}
                                                {tab === 'inactive' && <Option value="active">Active</Option>}
                                                {tab === 'inactive' && <Option value="waiting">Waiting</Option>}
                                                {tab === 'waiting' && <Option value="active">Active</Option>}
                                                {tab === 'waiting' && <Option value="inactive">Inactive</Option>}
                                                <Option value="delete">Delete</Option>
                                                <Option value="move">Move</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <Button
                                            variant="contained"
                                            onClick={goOnClick}
                                            style={{
                                                backgroundColor: "#cd4040",
                                                color: '#fff',
                                                height: 30
                                            }}
                                            className="jr-btn"
                                        >
                                            Go
                                        </Button>
                                    </div>
                                    <div className="col-md-3 text-right">
                                        <Link href={`/qbanksubcategory/questions/addEditQuestion?mainId=${mainCatId}&subId=${subCatId}&mainName=${mainCatName}&subName=${subCatName}`}>
                                            <a>
                                                <Button
                                                    variant="contained"
                                                    style={{
                                                        backgroundColor: "#80bc00",
                                                        color: '#fff',
                                                        height: 30
                                                    }}
                                                    className="jr-btn"
                                                >
                                                    <i className="fas fa-plus" />Add
                                                </Button>
                                            </a>
                                        </Link>
                                    </div>
                                    <div className="col-md-3">
                                        <Link href={`/qbanksubcategory/questions/addEditPassageQuestion?mainId=${mainCatId}&subId=${subCatId}&mainName=${mainCatName}&subName=${subCatName}`}>
                                            <a>
                                                <Button
                                                    variant="contained"
                                                    style={{
                                                        backgroundColor: "#80bc00",
                                                        color: '#fff',
                                                        height: 30
                                                    }}
                                                    className="jr-btn"
                                                >
                                                    <i className="fas fa-plus" />Add Passage
                                                </Button>
                                            </a>
                                        </Link>
                                    </div>
                                </div>
                                {action === 'move' &&
                                    <div className='row' style={{ marginBottom: 10 }}>
                                        <div className="col-lg-4 d-flex flex-column order-lg-1">
                                            <div className="form-group">
                                                <Select
                                                    onChange={searchTypeOnChange}
                                                    placeholder="Select Action"
                                                    className="ps-ant-dropdown"
                                                    style={{ width: '100%' }}
                                                    value={searchType}
                                                >
                                                    <Option value="">Q Bank Main Category</Option>
                                                    <Option value="T">Text</Option>
                                                    <Option value="I">Image</Option>
                                                    <Option value="P">Passage</Option>
                                                </Select>
                                            </div>
                                        </div>
                                        <div className="col-lg-4 d-flex flex-column order-lg-1">
                                            <div className="form-group">
                                                <Select
                                                    onChange={searchTypeOnChange}
                                                    placeholder="Select Action"
                                                    className="ps-ant-dropdown"
                                                    style={{ width: '100%' }}
                                                    value={searchType}
                                                >
                                                    <Option value="">Q Bank Sub Category</Option>
                                                    <Option value="T">Text</Option>
                                                    <Option value="I">Image</Option>
                                                    <Option value="P">Passage</Option>
                                                </Select>
                                            </div>
                                        </div>
                                    </div>
                                }
                                <div className='row'>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Select
                                                onChange={searchTypeOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: '100%' }}
                                                value={searchType}
                                            >
                                                <Option value="">Question Type</Option>
                                                <Option value="T">Text</Option>
                                                <Option value="I">Image</Option>
                                                <Option value="P">Passage</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Select
                                                onChange={sortDateOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: '100%' }}
                                                value={sortDate}
                                            >
                                                <Option value="">Sort By Date</Option>
                                                <Option value="ASC">Ascending</Option>
                                                <Option value="DESC">Decending</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Select
                                                onChange={searchLevelOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: '100%' }}
                                                value={searchLevel}
                                            >
                                                <Option value="">Difficulty Level</Option>
                                                <Option value="1">Level 1</Option>
                                                <Option value="2">Level 2</Option>
                                                <Option value="3">Level 3</Option>
                                                <Option value="4">Level 4</Option>
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Select
                                                onChange={searchFucaltyOnChange}
                                                placeholder="Select Action"
                                                className="ps-ant-dropdown"
                                                style={{ width: '100%' }}
                                                value={searchFucalty}
                                                showSearch={true}
                                                filterOption={(input, option) =>
                                                    option.children.toString().toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                }
                                            >
                                                <Option value="">Select Fuculty</Option>
                                                <Option value="1">qcadmin</Option>
                                                {fucaltyList.map(f => {
                                                    return (
                                                        <Option value={f.op_id} key={f.op_id}>{f.op_name}</Option>
                                                    )
                                                })}
                                            </Select>
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <input
                                                className="form-control"
                                                type="text"
                                                placeholder="Search"
                                                style={{ height: 33 }}
                                                value={search}
                                                onChange={(e) => searchOnChange(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <Button
                                                variant="contained"
                                                onClick={resetOnClick}
                                                style={{
                                                    backgroundColor: "#2196f3",
                                                    color: '#fff',
                                                    height: 30
                                                }}
                                                className="jr-btn"
                                            >
                                                <i className="fas fa-align-center" /> Reset
                                            </Button>
                                        </div>
                                    </div>
                                </div>
                                <div style={{ marginTop: 10 }}>
                                    <TableQuestions
                                        questions={tab === "active" ? activeQuestions : tab === "waiting" ? waitingQuestions : inactiveQuestions}
                                        onSelectAll={onSelectAll}
                                        onSelectOne={onSelectOne}
                                        selectAll={selectAll}
                                        selectedCatIds={selectedCatIds}
                                        mainCatId={mainCatId}
                                        subCatId={subCatId}
                                        mainCatName={mainCatName}
                                        subCatName={subCatName}
                                    />
                                </div>
                                <div style={{ margin: '10px auto', textAlign: 'right' }}>
                                    <Pagination
                                        total={tab === "active" ? activeQuestionCount : tab === "waiting" ? waitingQuestionCount : inactiveQuestionCount}
                                        defaultCurrent={1}
                                        current={currentPage}
                                        defaultPageSize={10}
                                        pageSize={pageSizeTotal}
                                        pageSizeOptions={['10', '20', '30']}
                                        onChange={pageSizeChange}
                                        showSizeChanger={true}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Spin>
        </div>
    );
};


export default Home;
