import React from 'react';
import { Checkbox, Table } from 'antd';
import Moment from "moment";

const TableExampackage= ({ allExampackage, editModalOnClick, onSelectAll, onSelectOne, selectAll, selectedHomeCatIds}) => {
  const columns = [
  
    {
      title: 'Package Name',
      dataIndex: 'PackageName',
      width: '9%',
    },
    {
      title: 'Master Category',
      dataIndex: 'MasterCategory',
      width: '12%',
    },
    {
      title: 'Main Category',
      dataIndex: 'MainCategory',
      width: '9%',
    },
    {
      title: 'Sub Category',
      dataIndex: 'SubCategory',
      width: '10%',
    },
    {
      title: 'Selling Price (INR)',
      dataIndex: 'SellingPrice',
      width: '10%',
    },
    {
      title: 'Package Date	',
      dataIndex: 'PackageDate',
      width: '10%',
    },
  
    {
      title: 'Edit',
      dataIndex: 'edit',
      align: 'center',
      width: '5%',
    },
    {
      title: 'Delete',
      dataIndex: 'delete',
      align: 'center',
      width: '5%',
    },
    {
      title: <Checkbox checked={selectAll} onClick={(e) => onSelectAll(e.target.checked)}></Checkbox>,
      dataIndex: 'check',
      width: '2%'
    }
  ];
  
  let data = allExampackage.map(a => {
    let obj = {
      key: a.package_id,
      PackageName: a.package_name,
      MasterCategory:a.mastercategory,
      MainCategory:a.maincategory,
      SubCategory:a.subcategory,
      SellingPrice:a.selling_price,
      PackageDate:Moment(a.package_date).format('DD-MM-YYYY'),

     
      edit: (<i className="fas fa-pen" onClick={() => editModalOnClick(a)} style={{ cursor: 'pointer' }}></i>),

      check: (
        <Checkbox
          onClick={() => onSelectOne(a.package_id)}
          checked={selectedHomeCatIds.indexOf(a.package_id) >= 0}
        />
      )
    }
    return (obj);
  });

  return (
    <div>
      <div>
        <Table columns={columns} dataSource={data} pagination={false} bordered />
      </div>
    </div>
  );
};

export default TableExampackage;
