import { all, put, call, takeEvery } from 'redux-saga/effects';

import { actionTypes, getAllCouponSuccess, getInactiveCouponSuccess } from './action';

import CouponRepository from '../../repositories/CouponRepository';

function* getAllCouponSaga({ payload }) {
    console.log('11')
    try {
        const data = yield call(CouponRepository.getAllCoupon, payload);
        console.log(data , '1111')
        yield put(getAllCouponSuccess(data));
    } catch (err) {
        console.log( err , '111112222')
        yield put(getAllCouponSuccess(null));
    }
}

function* getInactiveCouponSaga({ payload }) {
    console.log('22')
    try {
        const data = yield call(CouponRepository.getInactiveCoupon, payload);
        console.log(data , '222')
        yield put(getInactiveCouponSuccess(data));
    } catch (err) {
        console.log( err , '22233333')
        yield put(getInactiveCouponSuccess(null));
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_ALL_COUPON_REQUEST, getAllCouponSaga)]);
    yield all([takeEvery(actionTypes.GET_INACTIVE_COUPON_REQUEST, getInactiveCouponSaga)]);
}
